        translate([0, 0, -seat_h/2])
        cube([seat_l-10, seat_w-10, seat_h+1], center=true);

        // Rounded edges
        translate([seat_l/2-frame_t, 0, 0])
        cylinder(h=seat_h+1, d=frame_t*2);
        translate([-seat_l/2+frame_t, 0, 0])
        cylinder(h=seat_h+1, d=frame_t*2);
    }

