        // Vertical lines
        translate([5 + (table_top - 10) * i / (grid - 1) - 0.5, 5, table_height - 4.5])
            cube([1, table_top - 10, 1]);
        // Horizontal lines
        translate([5, 5 + (table_top - 10) * i / (grid - 1) - 0.5, table_height - 4.5])
            cube([table_top - 10, 1, 1]);
