// Main module
module kart_stool() {
    // Central frame structure
    frame();

    // Seat on top
    seat();

    // Four wheels
    translate([kart_length/2 - 15, kart_width/2 - 15, wheel_radius]) 
        wheel();
    translate([kart_length/2 - 15, -(kart_width/2 - 15), wheel_radius]) 
        wheel();
    translate([-(kart_length/2 - 15), kart_width/2 - 15, wheel_radius]) 
        wheel();
    translate([-(kart_length/2 - 15), -(kart_width/2 - 15), wheel_radius]) 
        wheel();

    // Support beams
    support_beams();

    // Steering column detail
    steering_column();
}

// Main frame structure
module frame() {
    // Front frame
    translate([kart_length/2 - 10, 0, wheel_radius + 10]) 
        cube([frame_thickness, kart_width - 20, 40], center=true);

    // Rear frame
    translate([-kart_length/2 + 10, 0, wheel_radius + 10]) 
        cube([frame_thickness, kart_width - 20, 40], center=true);

    // Side frames
    translate([0, kart_width/2 - 10, wheel_radius + 20]) 
        cube([kart_length - 20, frame_thickness, 20], center=true);
    translate([0, -kart_width/2 + 10, wheel_radius + 20]) 
        cube([kart_length - 20, frame_thickness, 20], center=true);
}

// Seat with realistic kart-style contours
module seat() {
    // Main seat platform
    translate([0, 0, kart_height - seat_thickness/2])
        cube([seat_length, seat_width, seat_thickness], center=true);

    // Seat backrest
    translate([0, 0, kart_height + 5])
        difference() {
            cube([seat_length - 10, seat_width - 10, 15], center=true);
            translate([0, 0, -5])
                cube([seat_length - 10, seat_width - 10, 20], center=true);
        }

    // Seat edge reinforcement
    for(x = [-seat_length/2 + 5, seat_length/2 - 5]) {
        for(y = [-seat_width/2 + 5, seat_width/2 - 5]) {
            translate([x, y, kart_height - seat_thickness/2])
                cylinder(h=seat_thickness, r=3, center=true);
        }
    }
}

// Wheels with spokes
module wheel() {
    // Tire
    difference() {
        cylinder(h=wheel_thickness, r=wheel_radius, center=true);
        cylinder(h=wheel_thickness + 2, r=wheel_radius - wall_thickness*2, center=true);
    }

    // Spokes
    for(i = [0:3]) {
        rotate([0, 0, i * 90])
            translate([wheel_radius/2, 0, 0])
                cube([wheel_radius - 5, wall_thickness, wheel_thickness], center=true);
    }

    // Hub
    cylinder(h=wheel_thickness + 1, r=axle_diameter * 2, center=true);
}

// Support beams between frame
module support_beams() {
    // Cross beams at wheel level
    translate([0, 0, wheel_radius + 5])
        cube([kart_length - 30, frame_thickness, frame_thickness], center=true);

    // Diagonal supports
    translate([kart_length/4, kart_width/4, wheel_radius + 20])
        rotate([0, 45, 0])
        cube([30, frame_thickness, frame_thickness], center=true);

    translate([-kart_length/4, -kart_width/4, wheel_radius + 20])
        rotate([0, -45, 0])
        cube([30, frame_thickness, frame_thickness], center=true);
}

// Steering column detail
module steering_column() {
    translate([kart_length/2 - 5, 0, kart_height - 10])
        cylinder(h=30, r=5, center=false);

    // Steering wheel detail
    translate([kart_length/2 - 5, 0, kart_height + 15])
        difference() {
            cylinder(h=8, r=12, center=true);
            cylinder(h=10, r=8, center=true);
        }
}

