// Jacks-inspired coffee table
// Realistic style, medium complexity
// Overall size: 120mm x 120mm x 45mm

$fn = 32;

// Main parameters
leg_length = 45;
leg_diameter = 8;
top_thickness = 6;
table_size = 120;
beam_diameter = 5;
joint_size = 12;
joint_height = 8;

// Central joint sphere
module central_joint() {
    // Main joint sphere
    sphere(d = joint_size);

    // Decorative rings
    for (i = [1:3]) {
        rotate([0, 90, 0])
            rotate([0, 0, i * 60])
                translate([0, 0, -joint_size/2 + i * 2])
                    cylinder(h = 1, d = joint_size + 2, $fn = 6);
    }
}

// Single leg with connection detail
module leg(angle_x, angle_z) {
    // Connection joint
    rotate([angle_x, 0, angle_z])
        translate([0, 0, joint_size/2])
            cylinder(h = joint_height, d = joint_size * 0.8);

    // Main leg
    rotate([angle_x, 0, angle_z])
        translate([0, 0, joint_size/2 + joint_height/2])
            cylinder(h = leg_length - joint_height/2 - joint_size/2, d = leg_diameter);

    // Foot detail
    rotate([angle_x, 0, angle_z])
        translate([0, 0, leg_length - joint_size/2])
            sphere(d = leg_diameter * 1.2);
}

// Cross beam between legs
module cross_beam(x1, y1, z1, x2, y2, z2) {
    dx = x2 - x1;
    dy = y2 - y1;
    dz = z2 - z1;
    length = sqrt(dx*dx + dy*dy + dz*dz);

    translate([x1, y1, z1])
        rotate([0, atan2(sqrt(dx*dx + dy*dy), dz), atan2(dy, dx)])
            cylinder(h = length, d = beam_diameter);
}

// Table top with beveled edge
module table_top() {
    difference() {
        // Main top
        cube([table_size, table_size, top_thickness], center = true);

        // Underside cavity for weight reduction
        translate([0, 0, -top_thickness/2 + 2])
            cube([table_size - 10, table_size - 10, top_thickness - 2], center = true);

        // Bevel edges
        for (dx = [-1, 1]) {
            for (dy = [-1, 1]) {
                translate([dx * (table_size/2 - 2), dy * (table_size/2 - 2), top_thickness/2 - 2])
                    rotate([0, 0, 45 * dx * dy])
                        cube([4, 4, 4], center = true);
            }
        }
    }

    // Surface texture pattern
    for (i = [0:5:table_size - 5]) {
        for (j = [0:5:table_size - 5]) {
            translate([-table_size/2 + i + 2.5, -table_size/2 + j + 2.5, top_thickness/2 - 0.5])
                cube([0.5, 0.5, 1], center = true);
        }
    }
}

// Assembly
union() {
    // Central joint
    central_joint();

    // Six legs in jacks formation
    for (angle = [0:60:300]) {
        leg(35, angle);
    }

    // Cross beams for stability
    for (angle = [30:120:360]) {
        x1 = cos(angle) * joint_size/2;
        y1 = sin(angle) * joint_size/2;
        z1 = joint_size/2;
        x2 = cos(angle) * (leg_length * 0.7) * sin(35);
        y2 = sin(angle) * (leg_length * 0.7) * sin(35);
        z2 = (leg_length * 0.7) * cos(35);
        cross_beam(x1, y1, z1, x2, y2, z2);
    }

    // Table top
    translate([0, 0, leg_length + top_thickness/2])
        table_top();

    // Mounting brackets
    for (angle = [0:60:300]) {
        mount_x = cos(angle) * joint_size/2 * sin(35);
        mount_y = sin(angle) * joint_size/2 * sin(35);
        mount_z = joint_size/2 * cos(35) + leg_length * 0.3;

        translate([mount_x, mount_y, mount_z])
            rotate([90 - 35, 0, angle])
                cylinder(h = 10, d = 6);
    }
}

