        translate([0, 0, table_height]) {
            cylinder(h = top_thickness, r = top_diameter/2, center = true);
        }

        // Top support ring
        translate([0, 0, table_height - top_thickness/2 - 1]) {
            cylinder(h = 2, r = top_diameter/2 - 5, center = true);
