    // Base ring for stability
    translate([0, 0, base_height/2]) {
        difference() {
            cylinder(h = base_height, r = top_diameter/2 - 10, center = true);
            cylinder(h = base_height + 1, r = top_diameter/2 - 10 - base_extension, center = true);
