// Base Structure
module lyre_base() {
    // Bottom crossbar
    translate([0, -lyre_height/2 + 10, 0])
        cube([lyre_width, 10, arm_thickness], center=true);

    // Top crossbar
    translate([0, lyre_height/2 - 15, 0])
        cube([lyre_width/2, 10, arm_thickness], center=true);
}

// Curved Arm Construction
module lyre_arm(side) {
    // Create curved arm using stacked cylinders
    arm_points = 20;
    for(i = [0:arm_points-1]) {
        angle = i * 180 / (arm_points-1);
        x = cos(angle) * (lyre_width/2 - arm_width) * side;
        y = sin(angle) * (lyre_height/2 - 20);
        radius = arm_width/2 * (1 - i/arm_points * 0.3); // Tapering

        translate([x, y - lyre_height/2 + 10, 0])
            cylinder(h=arm_thickness, r=radius, $fn=8);
    }
}

// String System
module lyre_strings() {
    string_count = 7;
    string_spacing = (lyre_width - 20) / (string_count - 1);

    for(i = [0:string_count-1]) {
        x_pos = -lyre_width/2 + 10 + i * string_spacing;

        // String (thin cylinder)
        translate([x_pos, 0, arm_thickness/2])
            rotate([90, 0, 0])
                cylinder(h=lyre_height - 25, r=0.5, $fn=6);

        // String anchor at bottom
        translate([x_pos, -lyre_height/2 + 10, 0])
            cylinder(h=2, r=1.2, $fn=8);

        // String tuner at top
        translate([x_pos, lyre_height/2 - 15, arm_thickness/2])
            rotate([90, 0, 0])
                cylinder(h=3, r=1.5, $fn=8);
    }
}

