    // Main body - hollowed box
    difference() {
        // Outer shell
        translate([0, -lyre_height/4, -arm_thickness/2 - 8])
            cube([lyre_width - 10, lyre_height/2, 16], center=true);

        // Hollow interior
        translate([0, -lyre_height/4, -arm_thickness/2 - 6])
            cube([lyre_width - 14, lyre_height/2 - 4, 12], center=true);

        // Sound hole
        translate([0, -lyre_height/3, -arm_thickness/2 - 4])
            cylinder(h=10, r=8, $fn=16);
    }

    // Decorative edge around sound hole
    translate([0, -lyre_height/3, -arm_thickness/2 - 4])
        cylinder(h=1, r=10, $fn=16);
}

// Support Brackets
module support_brackets() {
    // Left bracket
    translate([-lyre_width/2 + 5, 0, 0])
        difference() {
            cube([10, lyre_height/2, arm_thickness]);
            translate([5, lyre_height/4, arm_thickness/2])
                rotate([0, 90, 0])
                    cylinder(h=10, r=2, $fn=8);
        }

    // Right bracket
    translate([lyre_width/2 - 5, 0, 0])
        difference() {
            cube([10, lyre_height/2, arm_thickness]);
            translate([-5, lyre_height/4, arm_thickness/2])
                rotate([0, 90, 0])
                    cylinder(h=10, r=2, $fn=8);
        }
}

