module table_legs() {
    // Four legs at corners
    for (x = [-1, 1]) {
        for (y = [-1, 1]) {
            translate([
                x * (table_width/2 - leg_size/2 - 5),
                y * (table_depth/2 - leg_size/2 - 5),
                0
            ]) {
                // Leg with taper
                cylinder(h = table_height - wall_thickness, r1 = leg_size/2, r2 = leg_size/2 - 1);
            }
        }
    }
}

module table_frame() {
    // Bottom frame connecting legs
    difference() {
        // Outer frame
        cube([table_width, table_depth, wall_thickness], center = true);

        // Inner cutout
        translate([0, 0, -1])
            cube([table_width - leg_size*2, table_depth - leg_size*2, wall_thickness + 2], center = true);
    }
}

module map_surface() {
    // Base table top
    difference() {
        cube([table_width, table_depth, wall_thickness], center = true);

        // Recess for relief
        translate([0, 0, -wall_thickness/2])
            cube([table_width - 8, table_depth - 8, wall_thickness*2], center = true);
    }

    // Topographic features
    translate([0, 0, wall_thickness/2]) {
        // Mountain range (triangular prism)
        translate([-table_width/4, 0, 0]) {
            rotate([90, 0, 0])
                linear_extrude(height = table_depth/3)
                    polygon(points=[
                        [0, 0],
                        [20, max_relief],
                        [40, 0]
                    ]);
        }

        // Hills (spherical bumps)
        for (i = [0:3]) {
            translate([
                table_width/4 + i*15 - 30,
                table_depth/4 - i*5,
                0
            ]) {
                scale([1.5, 1, 0.5])
                    sphere(r = max_relief/2);
            }
        }

        // Valley (cylindrical depression)
        translate([table_width/3, -table_depth/4, 0]) {
            rotate_extrude()
                polygon(points=[
                    [0, 0],
                    [15, -max_relief/2],
                    [20, 0]
                ]);
        }

        // River (thin cylinder)
        translate([table_width/6, -table_depth/3, 0]) {
            rotate([90, 0, 0])
                cylinder(h = table_depth*2/3, r = 2, center = true);
        }

        // Compass rose detail
        translate([table_width/2 - 15, table_depth/2 - 15, 0]) {
            // Base circle
            cylinder(h = max_relief/2, r = 8);

            // North arrow
            translate([0, 0, max_relief/2])
                rotate([0, 0, 0])
                    cube([2, 6, max_relief/2], center = true);

            // East arrow
            translate([0, 0, max_relief/2])
                rotate([0, 0, 90])
                    cube([2, 6, max_relief/2], center = true);
        }
    }
}

module table() {
    // Assemble table
    union() {
        // Legs and frame
        table_legs();

        translate([0, 0, table_height/2 - wall_thickness/2])
            table_frame();

        // Table top with map
        translate([0, 0, table_height - wall_thickness/2])
            map_surface();
    }
}

