// Main globe module
module globe() {
    difference() {
        // Outer sphere
        sphere(r = globe_radius);

        // Hollow interior
        sphere(r = globe_radius - wall_thickness);

        // South pole opening for stand
        rotate([180, 0, 0]) 
            cylinder(h = wall_thickness + 2, r = 8, center = true);
    }

    // Latitude lines (decorative)
    for(lat = [-60:30:60]) {
        rotate([90, 0, 0])
        rotate([lat, 0, 0])
        difference() {
            rotate_extrude()
                translate([globe_radius - 0.5, 0, 0])
                    circle(r = 0.5);
            sphere(r = globe_radius - wall_thickness);
        }
    }

    // Longitude lines (decorative)
    for(lon = [0:30:150]) {
        rotate([0, lon, 0])
        difference() {
            rotate_extrude()
                translate([globe_radius - 0.5, 0, 0])
                    circle(r = 0.5);
            sphere(r = globe_radius - wall_thickness);
        }
    }
}

