    // Main base plate
    difference() {
        cube([100, 100, base_thickness], center = true);
        cube([96, 96, base_thickness + 2], center = true);
    }

    // Corner supports
    for(x = [-45, 45])
    for(y = [-45, 45])
        translate([x, y, 0])
            cylinder(h = base_thickness, r = 5, center = true);

    // Center support for stand
    difference() {
        cylinder(h = base_thickness, r = 15, center = true);
        cylinder(h = base_thickness + 2, r = 6, center = true);
    }

    // Decorative feet
    for(pos = [[-40, -40], [40, -40], [-40, 40], [40, 40]]) {
