// Abacus Desk Organizer - Realistic Style
$fn = 32;

// Overall dimensions
length = 120;
width = 80;
height = 40;
frame_thickness = 3;
bead_diameter = 12;
bead_spacing = 15;
rod_diameter = 3;
leg_height = 15;
leg_taper = 0.8;

// Frame module
module abacus_frame() {
    // Bottom frame
    translate([0, 0, 0])
        cube([length, width, frame_thickness], center = true);

    // Top frame
    translate([0, 0, height - frame_thickness])
        cube([length, width, frame_thickness], center = true);

    // Left side
    translate([-length/2 + frame_thickness/2, 0, height/2])
        cube([frame_thickness, width - frame_thickness*2, height], center = true);

    // Right side
    translate([length/2 - frame_thickness/2, 0, height/2])
        cube([frame_thickness, width - frame_thickness*2, height], center = true);

    // Center support beams
    for (i = [-1, 1]) {
        translate([0, (width/4)*i, height/2])
            cube([length - frame_thickness*2, frame_thickness, height - frame_thickness*2], center = true);
    }
}

// Bead module
module bead(pos_x, pos_y, pos_z) {
    translate([pos_x, pos_y, pos_z])
        sphere(d = bead_diameter);
}

// Rod module
module rod(pos_x) {
    // Vertical rod
    translate([pos_x, 0, height/2])
        cylinder(h = height - frame_thickness*2, d = rod_diameter, center = true);

    // Beads on rod
    for (i = [0:3]) {
        bead(pos_x, 0, frame_thickness + bead_diameter/2 + i*bead_spacing);
    }
}

// Leg module with taper
module leg(pos_x, pos_y) {
    translate([pos_x, pos_y, -leg_height/2])
        cylinder(h1 = leg_height, h2 = leg_height*leg_taper, d1 = 8, d2 = 6, center = true);
}

// Main assembly
union() {
    // Frame structure
    abacus_frame();

    // Rods with beads
    for (x = [-length/2 + 20 : 20 : length/2 - 20]) {
        rod(x);
    }

    // Support legs
    leg(-length/2 + 15, -width/2 + 15);
    leg(length/2 - 15, -width/2 + 15);
    leg(-length/2 + 15, width/2 - 15);
    leg(length/2 - 15, width/2 - 15);

    // Small decorative feet pads
    translate([0, 0, -leg_height - 2])
        cube([length - 10, width - 10, 2], center = true);
}

