// Realistic Aloe Plant - Medium Complexity
// 3D printable with 2mm walls and no overhangs

$fn = 32;

// Main parameters
pot_height = 40;
pot_radius = 30;
plant_height = 120;
base_width = 5; // wall thickness for 3D printing

module pot() {
    // Outer pot
    difference() {
        cylinder(h=pot_height, r=pot_radius, center=false);
        translate([0, 0, 2]) // Base thickness
            cylinder(h=pot_height, r=pot_radius-3, center=false);
    }

    // Drainage hole
    translate([0, 0, 2])
        cylinder(h=3, r=5, center=false);

    // Soil surface
    translate([0, 0, pot_height-5])
        cylinder(h=5, r=pot_radius-3, center=false);
}

module leaf(height, width, thickness, angle) {
    // Tapered leaf shape using scaled cylinders
    union() {
        // Main leaf body
        translate([0, 0, height/2])
            scale([width/2, thickness/2, height/2])
                sphere(r=1);

        // Leaf tip - triangular
        translate([0, 0, height])
            rotate([90, 0, 0])
                cylinder(h=width*0.7, r1=width/2, r2=0, center=false);
    }
}

module aloe_rosette() {
    // Central leaves - largest
    for(i=[0:5]) {
        angle = i * 60;
        rotate([0, 0, angle])
            translate([0, 0, pot_height])
                rotate([10 + i*2, 0, 0])
                    leaf(plant_height*0.8, 20, 4, angle);
    }

    // Middle ring leaves
    for(i=[0:8]) {
        angle = i * 45;
        rotate([0, 0, angle])
            translate([0, 0, pot_height+5])
                rotate([15 + i*1.5, 0, 0])
                    leaf(plant_height*0.6, 15, 3, angle);
    }

    // Outer small leaves
    for(i=[0:12]) {
        angle = i * 30;
        rotate([0, 0, angle])
            translate([0, 0, pot_height+8])
                rotate([20 + i*1, 0, 0])
                    leaf(plant_height*0.4, 10, 2, angle);
    }

    // Baby shoots
    translate([15, 15, pot_height+3])
        rotate([0, 0, 45])
            leaf(plant_height*0.3, 8, 2, 45);

    translate([-15, -10, pot_height+3])
        rotate([0, 0, -30])
            leaf(plant_height*0.25, 7, 2, -30);
}

module aloe_texture() {
    // Leaf surface details - subtle ridges
    for(leaf=[0:20]) {
        rotate([0, 0, leaf*18])
            translate([0, 0, pot_height+leaf*0.5])
                rotate([5+leaf, 0, 0])
                    cylinder(h=plant_height*0.7, r=0.5, center=false);
    }
}

// Assembly
union() {
    pot();
    aloe_rosette();
}

// Base for printing
translate([0, 0, -2])
    cube([pot_radius*2.5, pot_radius*2.5, 2], center=true);
