$fn = 32;

// Overall scale
scale(0.4) {

// Body
color([1, 0.8, 0]) {
    union() {
        // Torso
        translate([0, 0, 25])
            scale([1.2, 1, 1.4])
            sphere(20);

        // Belly
        color([1, 1, 0.85])
        translate([0, -19, 20])
            scale([1.1, 0.3, 1.2])
            sphere(18);
    }
}

// Head
color([1, 0.8, 0]) {
    translate([0, 0, 55])
        sphere(22);
}

// Ears
module ear(x) {
    color([1, 0.8, 0]) {
        translate([x, 0, 70])
            rotate([0, x*3, 0])
            scale([0.3, 0.1, 1.2])
            cylinder(20, 8, 3);
    }
    color([0.2, 0.2, 0.2]) {
        translate([x, 0, 85])
            rotate([0, x*3, 0])
            scale([0.25, 0.08, 0.7])
            cylinder(8, 6, 2);
    }
}
ear(12);
ear(-12);

// Eyes
module eye(x) {
    color([0.1, 0.1, 0.2])
    translate([x, -20, 55])
        scale([1, 1.2, 0.5])
