// Mouth
color([0.3, 0.2, 0.2])
translate([0, -22, 48])
    rotate([90, 0, 0])
    cylinder(0.5, 5, 2, $fn=3);

// Arms
module arm(x) {
    color([1, 0.8, 0])
    translate([x, -15, 35])
        rotate([0, 30*x, 10])
        scale([0.4, 0.4, 1])
        cylinder(15, 5, 4);
}
