// Blastoise OpenSCAD model - medium complexity realistic style
// Overall size: ~80mm tall

module blastoise() {
    color([0.2,0.5,0.8]) union() {
        // Main body shell
        translate([0,0,15]) scale([1.2,1,1]) sphere(d=35, $fn=20);

        // Belly plate
        translate([0,-8,15]) rotate([90,0,0]) cylinder(h=10, d1=15, d2=20, $fn=20);

        // Head
        translate([0,12,35]) scale([0.8,0.7,0.8]) sphere(d=20, $fn=20);

        // Arms
        for(side=[-1,1]) {
            translate([side*15,5,25]) rotate([0,side*30,0]) {
                color([0.2,0.5,0.8]) cylinder(h=15, d=10, $fn=8);
                translate([0,0,15]) rotate([0,0,side*45]) cylinder(h=15, d=8, $fn=8);
            }
        }

        // Legs
        for(side=[-1,1]) {
            translate([side*10,-10,0]) {
                color([0.2,0.5,0.8]) cylinder(h=15, d=12, $fn=8);
                translate([0,0,15]) color([0.3,0.3,0.3]) sphere(d=14, $fn=15);
            }
        }

        // Tail
        translate([0,-15,12]) rotate([0,0,180]) cylinder(h=20, d=10, d1=8, $fn=8);

        // Water cannons
        for(side=[-1,1]) {
            translate([side*12,0,42]) rotate([0,side*15,0]) {
                color([0.4,0.4,0.4]) union() {
                    cylinder(h=15, d=8, $fn=12);
                    translate([0,0,15]) cylinder(h=10, d=6, $fn=12);
                }
            }
        }

        // Ears
        for(side=[-1,1]) {
            translate([side*8,18,40]) rotate([0,side*15,-30]) 
                color([0.2,0.5,0.8]) cylinder(h=8, d=5, $fn=6);
        }
    }

    // Eyes
    for(side=[-1,1]) {
        color([1,1,1]) translate([side*6,18,37]) sphere(d=4);
        color([0,0,0]) translate([side*6,19,37]) sphere(d=2);
    }

    // Mouth
    color([0.2,0.2,0.2]) translate([0,20,33]) scale([1,0.5,0.5]) sphere(d=5);

    // Shell ridges
    for(i=[-2:2]) {
        color([0.3,0.6,0.9]) translate([i*6,0,15]) rotate([90,0,0]) 
            cylinder(h=0.5, d=25-abs(i)*3, $fn=20);
    }
}

// Render the model
blastoise();

// Realistic Medium-Complexity Venusaur OpenSCAD Model
// Approx 80 mm tall, 3D-printable

// Color constants
body_green = [0.25,0.5,0.2];
flower_pink = [0.9,0.4,0.6];
leaf_green = [0.1,0.6,0.2];
eye_red = [0.8,0.1,0.1];
skin_beige = [0.9,0.8,0.7];

// Main body
module body() {
    color(body_green)
    union() {
        // Torso
        translate([0,0,25])
            scale([1.2,1,1.1])
            sphere(20,$fn=32);

        // Belly
        color(skin_beige)
        translate([0,-18,15])
            scale([0.8,0.4,0.6])
            sphere(15,$fn=24);

        // Back hump
        translate([0,5,30])
            scale([1.3,1.2,0.8])
            sphere(15,$fn=24);
    }
}

// Flower on back
module flower() {
    translate([0,0,45])
    rotate([0,0,0])
    union() {
        // Stem
        color(leaf_green)
        cylinder(h=15,r1=4,r2=6,$fn=12);

        // Petals
        color(flower_pink)
        for(i=[0:5]) {
            rotate([0,0,i*60])
            translate([0,12,15])
            rotate([90,0,0])
            scale([1.5,0.5,1])
            cylinder(h=8,r1=0,r2=6,$fn=12);
        }

        // Center
        color([0.4,0.2,0.3])
        translate([0,0,15])
        sphere(4,$fn=16);
    }
}

// Leg module
module leg(side) {
    translate([side*12,0,0])
    union() {
        // Upper leg
        color(body_green)
        translate([0,0,12])
            rotate([0,-side*10,0])
            scale([0.8,0.8,1.2])
            cylinder(h=20,r1=6,r2=5,$fn=12);

        // Foot
        translate([0,-2,0])
            scale([1.5,2,0.8])
            sphere(5,$fn=12);
    }
}

// Arm module
module arm(side) {
    translate([side*18,0,28])
    rotate([0,0,side*20])
    union() {
        // Upper arm
        color(body_green)
        rotate([0,0,90])
        scale([0.8,0.8,1.2])
        cylinder(h=15,r1=5,r2=4,$fn=12);

        // Vine
        color(leaf_green)
        translate([0,side*8,0])
        rotate([90,0,0])
        cylinder(h=20,r1=1.5,$fn=8);
    }
}

// Head
module head() {
    color(body_green)
    union() {
        // Main head
        translate([0,-30,40])
        scale([1.1,1.3,1])
        sphere(15,$fn=32);

        // Snout
        translate([0,-45,38])
        scale([0.8,0.6,0.6])
        sphere(10,$fn=24);

        // Eyes
        color(eye_red)
        translate([-8,-38,45])
        scale([0.8,1,1])
        sphere(3,$fn=12);
        translate([8,-38,45])
        scale([0.8,1,1])
        sphere(3,$fn=12);

        // Eye whites
        color([1,1,1])
        translate([-7,-39,46])
        sphere(1,$fn=8);
        translate([7,-39,46])
        sphere(1,$fn=8);

        // Ears
        color(body_green)
        translate([-12,-20,52])
        rotate([0,0,-30])
        scale([0.5,1.2,2])
        sphere(5,$fn=12);
        translate([12,-20,52])
        rotate([0,0,30])
        scale([0.5,1.2,2])
        sphere(5,$fn=12);

        // Mouth
        color([0.2,0.1,0.1])
        translate([0,-48,35])
        cube([8,1,2],center=true);
    }
}

// Leaf modules for back
module back_leaf(x,y,angle) {
    color(leaf_green)
    translate([x,y,40])
    rotate([0,0,angle])
    scale([1,0.1,2])
    cylinder(h=15,r1=0,r2=8,$fn=3);
}

// Assembly
scale(0.8)
union() {
    body();

    // Legs
    leg(1);
    leg(-1);

    // Arms
    arm(1);
    arm(-1);

    // Head
    head();

    // Flower
    flower();

    // Back leaves
    back_leaf(15,15,45);
    back_leaf(-15,15,-45);
    back_leaf(0,20,0);
}

// Mewtwo - Realistic Medium Complexity
// 3D printable model with no overhangs

$fn = 32;

// Colors
color_purple = [0.4, 0.2, 0.6];
color_body = [0.85, 0.8, 0.9];
color_stomach = [0.7, 0.7, 0.9];
color_eyes = [0.9, 0.9, 0.1];

// Main module
module mewtwo() {
    scale(0.5)
    union() {
        // Head
        translate([0, 0, 35])
        head();

        // Neck
        translate([0, 0, 25])
        cylinder(h=10, r1=8, r2=6);

        // Body
        translate([0, 0, 12])
        body();

        // Arms
        translate([12, 0, 22])
        rotate([0, 0, 15])
        arm();

        translate([-12, 0, 22])
        rotate([0, 0, -15])
        mirror([1, 0, 0])
        arm();

        // Legs
        translate([6, 0, 0])
        leg();

        translate([-6, 0, 0])
        mirror([1, 0, 0])
        leg();

        // Tail
        translate([0, -12, 8])
        rotate([0, 15, 0])
        tail();

        // Tubes
        for(i = [-1, 1]) {
            translate([i*8, 8, 18])
            rotate([90, 0, 0])
            tube();
        }
    }
}

module head() {
    color(color_body)
    union() {
        // Main head
        sphere(r=12);

        // Snout
        translate([0, 8, -2])
        scale([1, 1.5, 0.8])
        sphere(r=6);

        // Eyes
        color(color_eyes)
        for(i = [-1, 1]) {
            translate([i*6, 5, 3])
            rotate([0, 0, 0])
            sphere(r=2.5);

            // Eye pupils
            color([0, 0, 0])
            translate([i*6.5, 5.5, 3.5])
            sphere(r=1);
        }

        // Ears
        color(color_body)
        for(i = [-1, 1]) {
            translate([i*10, 0, 8])
            rotate([0, 30*i, 0])
            scale([1, 0.5, 2])
            cylinder(h=12, r1=4, r2=2);
        }

        // Purple markings
        color(color_purple)
        translate([0, 12, 0])
        rotate([90, 0, 0])
        cylinder(h=15, r=5);
    }
}

module body() {
    color(color_body)
    difference() {
        // Main torso
        scale([1.2, 1, 1.5])
        sphere(r=15);

        // Stomach area
        color(color_stomach)
        translate([0, 8, -6])
        scale([0.8, 0.7, 0.5])
        sphere(r=12);

        // Purple chest marking
        color(color_purple)
        translate([0, 10, -5])
        rotate([90, 0, 0])
        cylinder(h=8, r=6);
    }
}

module arm() {
    color(color_body)
    union() {
        // Upper arm
        rotate([0, 0, 0])
        cylinder(h=12, r1=4, r2=3);

        // Forearm
        translate([0, 12, 0])
        rotate([0, -30, 0])
        cylinder(h=10, r1=3, r2=2.5);

        // Hand
        translate([0, 20, -5])
        sphere(r=3);

        // Fingers
        for(i = [0:2]) {
            rotate([0, 0, i*30-30])
            translate([0, 2, 0])
            cylinder(h=3, r=0.8);
        }
    }
}

module leg() {
    color(color_body)
    union() {
        // Thigh
        cylinder(h=12, r1=5, r2=4);

        // Shin
        translate([0, 0, -12])
        rotate([0, 15, 0])
        cylinder(h=10, r1=4, r2=3);

        // Foot
        translate([0, -2, -22])
        scale([1.5, 2, 0.8])
        sphere(r=4);
    }
}

module tail() {
    color(color_purple)
    union() {
        // Base
        cylinder(h=5, r1=5, r2=4);

        // Main tail segments
        for(i = [0:3]) {
            translate([0, -i*8, -i*2])
            rotate([0, 10*i, 0])
            cylinder(h=8, r1=4-i*0.5, r2=3.5-i*0.5);
        }

        // Tail tip
        translate([0, -32, -8])
        sphere(r=2);
    }
}

module tube() {
    color(color_purple)
    difference() {
        // Outer tube
        cylinder(h=15, r=3);

        // Inner hole
        cylinder(h=16, r=2);
    }
}

// Generate model
mewtwo();

