module groudon_head() {
    color("#B22222") {
        // Head base
        translate([0, 20, 35]) 
            rotate([45, 0, 0]) 
                scale([1.2, 1, 1.2]) 
                    sphere(12, $fn=20);

        // Snout
        translate([0, 30, 35]) 
            scale([0.8, 1.5, 0.8]) 
                cylinder(h=8, r=6, $fn=10);
    }

    // White jaw
    color("#F5F5F5") {
        translate([0, 34, 30]) 
            scale([0.6, 0.3, 0.3]) 
