light_blue = [0.68, 0.85, 0.90];
dark_steel = [0.18, 0.31, 0.41];
silver = [0.75, 0.75, 0.75];
black = [0.1, 0.1, 0.1];

// Main body module
module dialga_body() {
    color(steel_blue)
    union() {
        // Torso
        translate([0, 0, 30])
            scale([1.2, 0.8, 1])
                sphere(r=20);

        // Chest plate
        translate([0, 15, 25])
            cube([30, 8, 25], center=true);

        // Back ridge
        for(i = [-1, 1])
            translate([i*15, -15, 35])
                cube([5, 10, 30], center=true);
    }
}

// Head module
module dialga_head() {
    color(steel_blue)
    union() {
        // Main head
        translate([0, 30, 45])
            scale([0.8, 1, 1])
                sphere(r=18);

        // Snout
        translate([0, 45, 40])
            scale([0.6, 1.2, 0.8])
                sphere(r=12);

        // Crest
        color(light_blue)
        translate([0, 25, 60])
            for(i = [-1, 0, 1])
                translate([i*8, 0, 0])
                    cube([3, 5, 12], center=true);

        // Eyes
        color(black)
        translate([-8, 40, 48])
            sphere(r=3);
        translate([8, 40, 48])
            sphere(r=3);
    }
}

// Leg modules
module dialga_legs() {
    color(steel_blue)
    union() {
        // Front legs
        for(side = [-1, 1]) {
            translate([side*15, 15, 15]) {
                // Upper leg
                rotate([0, 15, 0])
                    cylinder(h=20, r1=6, r2=5);

                // Lower leg
                translate([0, 0, -20])
                    rotate([0, -15, 0])
                        cylinder(h=15, r1=5, r2=4);

                // Foot
                translate([0, 0, -35])
                    scale([1.5, 2, 1])
                        sphere(r=4);
            }
        }

        // Back legs
        for(side = [-1, 1]) {
            translate([side*12, -15, 15]) {
                // Upper leg
                rotate([0, -10, 0])
                    cylinder(h=25, r1=7, r2=6);

                // Lower leg
                translate([0, 0, -25])
                    rotate([0, 10, 0])
                        cylinder(h=18, r1=6, r2=5);

                // Foot
                translate([0, 0, -43])
                    scale([2, 2.5, 1])
                        sphere(r=5);
            }
        }
    }
}

// Tail module
module dialga_tail() {
    color(steel_blue)
    union() {
        // Tail segments
        for(i = [0:4]) {
            translate([0, -25 - i*8, 30 - i*3])
                scale([1 - i*0.15, 1 - i*0.1, 0.8 - i*0.1])
                    sphere(r=12 - i*2);
        }

        // Tail spikes
        color(dark_steel)
        for(i = [1:3]) {
            translate([0, -30 - i*8, 35 - i*3])
                rotate([0, 0, 45])
                    cube([2, 15, 2], center=true);
        }
    }
}

// Shoulder armor
module shoulder_armor() {
    color(dark_steel)
    for(side = [-1, 1]) {
        translate([side*25, 20, 35])
            rotate([0, 0, side*30])
                cube([8, 12, 15], center=true);
    }
}

// Chest diamond
module chest_diamond() {
    color(light_blue)
    translate([0, 15, 30])
        scale([1, 0.5, 1])
            cylinder(h=3, r=8, $fn=6);
}

// Back plates
module back_plates() {
    color(dark_steel)
    for(i = [-1, 0, 1]) {
        translate([i*12, -5, 40])
            rotate([0, 15, 0])
                cube([10, 3, 8], center=true);
    }
}

// Assembly
union() {
    dialga_body();
    dialga_head();
    dialga_legs();
    dialga_tail();
    shoulder_armor();
    chest_diamond();
    back_plates();
}

// Palkia - Realistic Style, Medium Complexity
// Scaled for 3D printing ~80mm height

$fn = 32;

// Main colors
color_purple = [0.4, 0.2, 0.6];
color_light_purple = [0.6, 0.4, 0.8];
color_white = [0.9, 0.9, 0.95];
color_gray = [0.5, 0.5, 0.6];
color_red = [0.8, 0.1, 0.1];
color_gold = [0.85, 0.65, 0.15];

// Main body module
module palkia_body() {
    // Central torso
    color(color_purple)
    scale([1, 0.8, 1.2])
    sphere(15);

    // Chest plate
    color(color_white)
    translate([0, -12, 5])
    scale([1.2, 0.3, 1.5])
    sphere(12);

    // Shoulder spheres
    for(side = [-1, 1]) {
        color(color_light_purple)
        translate([side * 18, 0, 8])
        sphere(8);
    }
}

// Head module
module palkia_head() {
    // Main head
    color(color_purple)
    translate([0, -20, 25])
    scale([0.9, 1.1, 1.2])
    sphere(12);

    // Snout
    color(color_purple)
    translate([0, -30, 22])
    scale([0.6, 1.5, 0.8])
    sphere(8);

    // Eyes
    for(side = [-1, 1]) {
        color(color_red)
        translate([side * 6, -28, 28])
        sphere(3);

        // Eye highlights
        color(color_white)
        translate([side * 6.5, -29, 29])
        sphere(1);
    }

    // Crest
    color(color_gray)
    translate([0, -22, 38])
    scale([0.3, 0.3, 2])
    cylinder(15, 8, 2);

    // Crest rings
    for(i = [1:3]) {
        color(color_gold)
        translate([0, -22, 38 + i * 3])
        cylinder(0.5, 8 + i * 2, 8 + i * 2);
    }
}

// Arm module
module palkia_arm(side) {
    // Upper arm
    color(color_purple)
    translate([side * 25, -5, 8])
    rotate([0, 0, side * 20])
    scale([0.8, 0.8, 2])
    cylinder(12, 5, 4);

    // Lower arm
    color(color_purple)
    translate([side * 30, -5, -5])
    rotate([0, 0, side * 30])
    scale([0.7, 0.7, 1.5])
    cylinder(10, 4, 6);

    // Hand with pearl
    color(color_purple)
    translate([side * 35, -5, -16])
    sphere(5);

    color(color_white)
    translate([side * 37, -5, -16])
    sphere(3);

    // Shoulder armor
    color(color_gray)
    translate([side * 18, 0, 8])
    rotate([0, 90, 0])
    cylinder(3, 8, 8);
}

// Leg module
module palkia_leg(side) {
    // Upper leg
    color(color_purple)
    translate([side * 12, 0, -15])
    rotate([side * 10, 0, 0])
    scale([0.8, 0.8, 1.5])
    cylinder(15, 6, 5);

    // Lower leg
    color(color_purple)
    translate([side * 14, 0, -30])
    rotate([side * 15, 0, 0])
    scale([0.7, 0.7, 1.2])
    cylinder(12, 5, 6);

    // Foot
    color(color_purple)
    translate([side * 16, 0, -42])
    scale([1.2, 2, 1])
    cube([8, 12, 6], center = true);
}

// Tail module
module palkia_tail() {
    color(color_purple)
    translate([0, 20, -15])
    rotate([0, 0, 180])
    cylinder(25, 8, 4);

    // Tail fin
    color(color_gray)
    translate([0, 45, -15])
    rotate([90, 0, 0])
    linear_extrude(2)
    polygon(points=[[0,0],[-15,10],[0,20],[15,10]]);
}

// Back wings
module palkia_wings() {
    for(side = [-1, 1]) {
        // Wing base
        color(color_purple)
        translate([side * 20, 10, 5])
        rotate([0, side * 30, 0])
        scale([0.3, 2, 1])
        cube([8, 25, 15], center = true);

        // Wing armor
        color(color_gray)
        translate([side * 25, 10, 5])
        rotate([0, side * 30, 0])
        scale([0.2, 1.5, 0.8])
        cube([6, 20, 12], center = true);
    }
}

// Assembly
module palkia() {
    palkia_body();
    palkia_head();

    // Arms
    for(side = [-1, 1]) {
        palkia_arm(side);
    }

    // Legs
    for(side = [-1, 1]) {
        palkia_leg(side);
    }

    palkia_tail();
    palkia_wings();

    // Chest gems
    color(color_gold)
    translate([0, -12, 12])
    sphere(2);

    // Body markings
    for(i = [1:3]) {
        color(color_white)
        translate([0, -5 + i * 5, 10 - i * 2])
        rotate([90, 0, 0])
        cylinder(0.5, 5 + i, 5 + i);
    }
}

// Main assembly with scale for 3D printing
scale([0.8, 0.8, 0.8])
palkia();

// Giratina (Altered Forme) - Realistic Style, Medium Complexity
// Overall size: ~70mm tall

$fn = 36;
module giratina() {
    color([0.15, 0.15, 0.25]) {
        // Main body
        translate([0, 0, 20]) rotate([0, 0, 0]) body();
        // Head
        translate([0, 0, 55]) rotate([0, 0, 0]) head();
        // Six wings
        translate([25, 15, 40]) rotate([0, 0, 45]) wing();
        translate([-25, 15, 40]) rotate([0, 0, -45]) mirror([1,0,0]) wing();
        translate([25, -15, 40]) rotate([0, 0, -45]) wing();
        translate([-25, -15, 40]) rotate([0, 0, 45]) mirror([1,0,0]) wing();
        translate([15, 25, 35]) rotate([0, 0, 90]) wing();
        translate([-15, -25, 35]) rotate([0, 0, -90]) mirror([1,0,0]) wing();
        // Six legs
        translate([12, 12, 5]) rotate([0, 0, 30]) leg();
        translate([-12, 12, 5]) rotate([0, 0, -30]) mirror([1,0,0]) leg();
        translate([12, -12, 5]) rotate([0, 0, -30]) leg();
        translate([-12, -12, 5]) rotate([0, 0, 30]) mirror([1,0,0]) leg();
        translate([8, 0, 0]) rotate([0, 0, 0]) leg();
        translate([-8, 0, 0]) rotate([0, 0, 0]) mirror([1,0,0]) leg();
        // Tail
        translate([0, 0, 15]) rotate([0, 0, 0]) tail();
    }
}

module body() {
    difference() {
        union() {
            // Main torso
            scale([1.2, 1, 1.5]) sphere(12);
            // Chest armor
            translate([0, 12, 0]) scale([1.3, 0.5, 1.2]) sphere(10);
        }
        // Neck hole
        translate([0, 0, 15]) cylinder(10, 6, 6, true);
    }
}

module head() {
    union() {
        // Main head shape
        translate([0, 8, 0]) scale([1, 0.8, 1.2]) sphere(10);
        // Snout
        translate([0, 18, 0]) scale([0.8, 0.5, 0.6]) sphere(8);
        // Crest
        translate([0, 0, 12]) scale([0.3, 0.3, 1]) sphere(15);
        // Side horns
        translate([8, 5, 8]) rotate([0, 0, 30]) cylinder(10, 2, 0);
        translate([-8, 5, 8]) rotate([0, 0, -30]) cylinder(10, 2, 0);
        // Eyes
        color([0.9, 0.1, 0.1]) {
            translate([6, 12, 2]) scale([1, 0.5, 1]) sphere(3);
            translate([-6, 12, 2]) scale([1, 0.5, 1]) sphere(3);
        }
        // Gold markings
        color([0.8, 0.7, 0.2]) {
            translate([0, 12, 4]) scale([1.5, 0.2, 0.5]) sphere(3);
            translate([0, 8, 6]) scale([0.8, 0.2, 0.8]) sphere(3);
        }
    }
}

module wing() {
    difference() {
        union() {
            // Main wing blade
            translate([0, 0, 0]) scale([1, 0.2, 3]) sphere(8);
            // Red wing tips
            color([0.9, 0.1, 0.1]) {
                translate([0, 0, 20]) scale([0.8, 0.8, 0.5]) sphere(5);
            }
        }
        // Cutout pattern
        translate([0, 0, 10]) scale([0.5, 0.5, 0.5]) sphere(8);
    }
}

module leg() {
    union() {
        // Upper leg
        cylinder(12, 4, 3);
        // Lower leg
        translate([0, 0, -12]) rotate([20, 0, 0]) cylinder(10, 3, 2);
        // Foot
        translate([0, 0, -22]) rotate([45, 0, 0]) scale([1, 0.5, 0.3]) sphere(6);
    }
}

module tail() {
    difference() {
        union() {
            // Main tail segments
            for (i = [0:3]) {
                translate([0, 0, -i*8]) scale([1-i*0.15, 1-i*0.15, 1]) sphere(8);
            }
            // Tail spikes
            color([0.9, 0.1, 0.1]) {
                for (i = [0:3]) {
                    translate([0, 0, -i*8-4]) rotate([0, 0, 45+i*30]) 
                    scale([0.3, 0.3, 1]) sphere(5);
                }
            }
        }
        // Hollow center for printability
        for (i = [0:3]) {
            translate([0, 0, -i*8]) scale([0.7-i*0.15, 0.7-i*0.15, 1]) sphere(7);
        }
    }
}

