// Yveltal - Realistic Style, Medium Complexity
// Main body and wing structure

$fn = 30;

module yveltal() {
    color([0.2, 0.1, 0.1]) {
        // Main body core
        translate([0, 0, 15]) {
            difference() {
                scale([1.2, 0.8, 1.5]) sphere(10);
                translate([0, 0, -10]) cube([25, 25, 20], center=true);
            }
        }

        // Chest armor plate
        translate([0, 0, 20]) {
            rotate([90, 0, 0]) 
            cylinder(h=3, r1=8, r2=12);
        }

        // Head
        translate([0, -12, 28]) {
            scale([1.3, 1.5, 1.2]) sphere(8);
        }

        // Beak
        color([0.5, 0.2, 0.2]) {
            translate([0, -20, 26]) {
                rotate([30, 0, 0])
                scale([1, 2.5, 1]) cylinder(h=6, r1=3, r2=1);
            }
        }

        // Neck
        translate([0, -5, 25]) {
            rotate([-20, 0, 0])
            cylinder(h=12, r1=5, r2=4);
        }

        // Eyes
        color([1, 0.2, 0.2]) {
            translate([4, -12, 30]) sphere(2.5);
            translate([-4, -12, 30]) sphere(2.5);
        }

        // Eye markings
        color([0.1, 0.05, 0.05]) {
            translate([6, -12, 28]) scale([0.5, 1.5, 0.2]) sphere(3);
            translate([-6, -12, 28]) scale([0.5, 1.5, 0.2]) sphere(3);
        }

        // Wings
        module wing() {
            color([0.2, 0.1, 0.1]) {
                // Main wing structure
                translate([0, 0, 5]) {
                    rotate([0, 0, 30])
                    scale([2, 0.3, 8]) cube([1, 1, 1]);
                }

                // Wing claw
                translate([0, 0, 10]) {
                    rotate([0, 20, 0])
                    cylinder(h=5, r1=2, r2=1);
                }

                // Wing membrane
                color([0.3, 0.15, 0.15]) {
                    for(i = [1:3]) {
                        translate([i*4, 0, 5 + i*2]) {
                            rotate([0, 0, 30])
                            scale([0.8, 0.2, 4]) cube([1, 1, 1]);
                        }
                    }
                }
            }
        }

        // Left wing
        translate([-15, 0, 20]) {
            rotate([0, 0, -20])
            wing();
        }

        // Right wing
        translate([15, 0, 20]) {
            scale([-1, 1, 1])
            rotate([0, 0, -20])
            wing();
        }

        // Tail structure
        color([0.25, 0.1, 0.1]) {
            translate([0, 10, 15]) {
                rotate([20, 0, 0])
                cylinder(h=20, r1=5, r2=2);
            }

            // Tail feathers
            for(i = [0:2]) {
                translate([i*3 - 3, 25 - i*2, 18 - i*2]) {
                    rotate([20 + i*10, i*10, 0])
                    scale([0.3, 0.1, 4]) cube([1, 1, 1]);
                }
            }
        }

        // Legs
        module leg() {
            color([0.3, 0.12, 0.12]) {
                // Upper leg
                translate([0, 0, 0]) {
                    rotate([0, 30, 0])
                    cylinder(h=8, r1=2.5, r2=2);
                }

                // Lower leg
                translate([0, 0, -6]) {
                    rotate([0, 0, 10])
                    cylinder(h=6, r1=2, r2=1.5);
                }

                // Foot
                translate([0, 2, -10]) {
                    rotate([90, 0, 0])
                    cylinder(h=4, r1=2, r2=3);
                }

                // Claws
                color([0.4, 0.15, 0.15]) {
                    for(j = [-1, 0, 1]) {
                        translate([j*1.5, 2, -10]) {
                            rotate([90, 0, 0])
                            cylinder(h=2, r1=0.5, r2=0.2);
                        }
                    }
                }
            }
        }

        // Left leg
        translate([-6, 0, 8]) {
            rotate([0, 0, 10])
            leg();
        }

        // Right leg
        translate([6, 0, 8]) {
            rotate([0, 0, -10])
            scale([-1, 1, 1])
            leg();
        }

        // Horns
        color([0.15, 0.05, 0.05]) {
            translate([0, -18, 32]) {
                rotate([20, 0, 0])
                cylinder(h=8, r1=2, r2=1);
            }

            translate([-3, -18, 31]) {
                rotate([25, -10, -10])
                cylinder(h=7, r1=1.5, r2=0.5);
            }

            translate([3, -18, 31]) {
                rotate([25, 10, 10])
                cylinder(h=7, r1=1.5, r2=0.5);
            }
        }

        // Chest markings
        color([0.8, 0.1, 0.1]) {
            translate([0, 0, 22]) {
                scale([1, 1.5, 0.5]) 
                cylinder(h=1, r=4);
            }

            // Diamond pattern
            for(x = [-2, 2]) {
                translate([x, 0, 22]) {
                    rotate([90, 0, 45])
                    cube([3, 0.5, 3], center=true);
                }
            }
        }
    }
}

