        translate([0, 0, 35]) 
        rotate([90, 0, 0]) 
        linear_extrude(1) 
        polygon(points = [[-8, -8], [8, -8], [0, 8]]);

        // Back pattern
        translate([0, -18, 35]) 
        rotate([90, 0, 0]) 
        linear_extrude(1) 
        circle(10);
