                    cylinder(h = shell_thickness * 2, d1 = body_width * 0.85, d2 = body_width * 0.75);
                    // Hollow center
                    cylinder(h = shell_thickness * 2.1, d1 = body_width * 0.65, d2 = body_width * 0.55);
                }
        }

        // Middle band
        translate([0, 0, body_height * 0.5])
            difference() {
                cylinder(h = shell_thickness * 3, d = body_width * 0.95);
                cylinder(h = shell_thickness * 3.1, d = body_width * 0.75);
            }

        // Bottom shell segments
        for(i = [0:1]) {
            translate([0, 0, body_height * 0.3 + i * 12])
                difference() {
                    cylinder(h = shell_thickness * 2, d1 = body_width * 0.8, d2 = body_width * 0.9);
                    cylinder(h = shell_thickness * 2.1, d1 = body_width * 0.6, d2 = body_width * 0.7);
                }
        }
    }
}

module eyes() {
    color(color_details)
    union() {
        // Left eye
        translate([-body_width * 0.2, body_width * 0.35, body_height * 0.7])
            rotate([0, 10, 0])
            difference() {
                scale([1, 1.2, 0.5])
                    sphere(d = 6);
                // Eye slit
                translate([0, 0, -1])
                    cube([8, 2, 2], center = true);
            }

        // Right eye
        translate([body_width * 0.2, body_width * 0.35, body_height * 0.7])
            rotate([0, -10, 0])
            difference() {
                scale([1, 1.2, 0.5])
                    sphere(d = 6);
                translate([0, 0, -1])
                    cube([8, 2, 2], center = true);
            }
    }
}

module cocoon_texture() {
    color(color_shell)
    union() {
        // Diagonal ridge pattern
        for(angle = [0:30:150]) {
            rotate([0, 0, angle])
            translate([0, 0, body_height * 0.1])
            for(z = [0:5:body_height * 0.8]) {
                hull() {
                    translate([body_width * 0.35, 0, z])
                        sphere(d = 2);
                    translate([body_width * 0.4, 0, z + 2])
                        sphere(d = 2);
                }
            }
        }
    }
}

module attachment_point() {
    color(color_details)
    // Top loop for hanging
    translate([0, 0, body_height * 0.95])
        difference() {
            rotate_extrude()
                translate([body_width * 0.15, 0, 0])
                    circle(r = 3);
            // Hollow for string
            rotate_extrude()
                translate([body_width * 0.15, 0, 0])
                    circle(r = 1.5);
        }
}

