// Colors
color_body = [0.8, 0.2, 0.1];       // Red-brown
color_wing = [0.6, 0.4, 0.3];       // Dark brown
color_beak = [0.9, 0.7, 0.2];       // Yellow
color_eye = [0.1, 0.1, 0.9];        // Blue
color_white = [1, 1, 1];
color_black = [0, 0, 0];

// Parameters
scale_factor = 0.8;
base_height = 80 * scale_factor;
body_radius = 15 * scale_factor;
head_radius = 12 * scale_factor;
beak_length = 18 * scale_factor;
wing_span = 40 * scale_factor;

// Module for rounded cube (body shaping)
module rounded_cube(size, radius) {
    hull() {
        for(dx = [0, size.x - radius*2])
        for(dy = [0, size.y - radius*2])
        for(dz = [0, size.z - radius*2])
            translate([dx + radius, dy + radius, dz + radius])
                sphere(r = radius);
    }
}

// Main body
module body() {
    color(color_body)
    union() {
        // Main body sphere
        sphere(r = body_radius);

        // Chest protrusion
        translate([0, -body_radius*0.7, -body_radius*0.3])
            scale([1.2, 0.8, 0.9])
                sphere(r = body_radius * 0.7);
    }
}

// Head and beak
module head_and_beak() {
    color(color_body)
    union() {
        // Head
        translate([0, body_radius + head_radius*0.8, body_radius*0.5])
            sphere(r = head_radius);

        // Beak
        color(color_beak)
        translate([0, body_radius + head_radius*0.8 + beak_length*0.6, body_radius*0.5])
            rotate([0, 90, 0])
                cylinder(h = beak_length, r1 = head_radius*0.3, r2 = head_radius*0.1);
    }
}

// Crest feathers
module crest() {
    color(color_body)
    for(i = [0:2]) {
        rotate([0, 0, (i-1)*25])
            translate([0, body_radius + head_radius*0.8 + 2, body_radius*0.5 + head_radius*0.8])
                cylinder(h = 8 * scale_factor, r1 = 2 * scale_factor, r2 = 1 * scale_factor);
    }
}

// Eyes
module eyes() {
    // Left eye
    translate([head_radius*0.5, body_radius + head_radius*0.7, body_radius*0.5 + head_radius*0.3])
        color(color_white)
        sphere(r = head_radius * 0.25);

    translate([head_radius*0.5, body_radius + head_radius*0.7 + 0.5, body_radius*0.5 + head_radius*0.3])
        color(color_eye)
        sphere(r = head_radius * 0.15);

    translate([head_radius*0.5 + 1, body_radius + head_radius*0.7 + 1, body_radius*0.5 + head_radius*0.3 + 1])
        color(color_black)
        sphere(r = head_radius * 0.08);

    // Right eye (mirror of left)
    mirror([1, 0, 0]) {
        translate([head_radius*0.5, body_radius + head_radius*0.7, body_radius*0.5 + head_radius*0.3])
            color(color_white)
            sphere(r = head_radius * 0.25);

        translate([head_radius*0.5, body_radius + head_radius*0.7 + 0.5, body_radius*0.5 + head_radius*0.3])
            color(color_eye)
            sphere(r = head_radius * 0.15);

        translate([head_radius*0.5 + 1, body_radius + head_radius*0.7 + 1, body_radius*0.5 + head_radius*0.3 + 1])
            color(color_black)
            sphere(r = head_radius * 0.08);
    }
}

// Wings
module wings() {
    // Left wing
    color(color_wing)
    translate([-body_radius*0.8, 0, body_radius*0.3])
        rotate([0, 30, 20])
            scale([1, 2.5, 0.3])
                sphere(r = wing_span * 0.3);

    // Wing tip
    translate([-body_radius*0.8 - wing_span*0.6, -body_radius*0.2, body_radius*0.3 + wing_span*0.15])
        rotate([0, 30, 45])
            scale([0.5, 1.5, 0.2])
                sphere(r = wing_span * 0.25);

    // Right wing (mirror)
    mirror([1, 0, 0])
    union() {
        color(color_wing)
        translate([-body_radius*0.8, 0, body_radius*0.3])
            rotate([0, 30, 20])
                scale([1, 2.5, 0.3])
                    sphere(r = wing_span * 0.3);

        translate([-body_radius*0.8 - wing_span*0.6, -body_radius*0.2, body_radius*0.3 + wing_span*0.15])
            rotate([0, 30, 45])
                scale([0.5, 1.5, 0.2])
                    sphere(r = wing_span * 0.25);
    }
}

// Tail feathers
module tail() {
    color(color_wing)
    for(i = [0:2]) {
        rotate([0, 0, (i-1)*15 - 90])
            translate([0, body_radius + 5, -body_radius*0.7])
                scale([0.8, 2.5, 0.2])
                    sphere(r = body_radius * 0.6);
    }
}

// Legs and feet
module legs() {
    // Left leg
    color(color_beak)
    translate([-body_radius*0.3, 0, -body_radius*0.9])
        cylinder(h = body_radius * 0.7, r = body_radius * 0.15);

    // Left foot
    translate([-body_radius*0.3, -body_radius*0.3, -body_radius*1.6])
        rotate([0, 0, -30])
            scale([2, 0.5, 0.3])
                cube([body_radius*0.4, body_radius*0.2, body_radius*0.1]);

    // Right leg
    translate([body_radius*0.3, 0, -body_radius*0.9])
        cylinder(h = body_radius * 0.7, r = body_radius * 0.15);

    // Right foot
    translate([body_radius*0.3, -body_radius*0.3, -body_radius*1.6])
        rotate([0, 0, 30])
            scale([2, 0.5, 0.3])
                cube([body_radius*0.4, body_radius*0.2, body_radius*0.1]);
}

// Assembly
union() {
    body();
    head_and_beak();
    crest();
    eyes();
    wings();
    tail();
    legs();
}

