                    }
                }
            }

            wing(1);
            wing(-1);

            // Tail feathers
            translate([0, 15, 25]) {
                color(color_red)
                rotate([-20, 0, 0]) {
                    for(i = [-1:1]) {
                        translate([i * 6, 0, i * 2]) {
                            rotate([0, 0, i * 10])
                            scale([0.2, 2, 0.1])
                            cylinder(h = 20, r1 = 3, r2 = 1);
                        }
                    }
                }
            }

            // Legs
            module leg(side) {
                translate([side * 8, 0, 15]) {
                    color(color_brown)
                    rotate([30, 0, side * 10])
                    cylinder(h = 18, r1 = 3, r2 = 2);

