                    translate([0, 8, -12]) {
                        color(color_beak)
                        rotate([90, 0, 0])
                        scale([1, 0.5, 1])
                        cylinder(h = 8, r1 = 4, r2 = 3);

                        // Toes
                        for(j = [-1, 0, 1]) {
                            translate([j * 3, 4, 0]) {
                                rotate([0, 0, j * 20])
                                scale([0.3, 0.3, 1])
                                cylinder(h = 5, r1 = 2, r2 = 1);
                            }
                        }
                    }
                }
            }

            leg(1);
            leg(-1);
        }
    }
}

