    scale([1.2, 1, 1])
    sphere(r=15);

    // Head - lighter blue sphere
    color("deepskyblue")
    translate([0, 0, 35])
    sphere(r=12);

    // Large petals - red with white spots
    module petal(radius, height, angle) {
        rotate([0, 0, angle])
        translate([0, radius/2, 40])
        rotate([90, 0, 0])
        scale([1.2, 0.3, 1])
        cylinder(h=height, r1=radius, r2=radius*0.8);
    }

    // Create 5 main petals
    for(i = [0:72:360]) {
        color("red")
        petal(18, 25, i);

        // White spots on petals
        color("white")
        for(j = [0.4, 0.7]) {
            translate([
                15*cos(i)*j, 
                15*sin(i)*j, 
                45 + j*5
            ])
            sphere(r=1.5);
        }
    }

    // Smaller inner petals - yellow
    module inner_petal(angle) {
        rotate([0, 0, angle])
        translate([0, 8, 42])
        rotate([90, 0, 0])
        scale([0.7, 0.2, 1])
        cylinder(h=12, r1=8, r2=6);
    }

    for(i = [36:72:360]) {
        color("gold")
        inner_petal(i);
    }

    // Eyes - large oval shape
    module eye(x_offset) {
        color("black")
        translate([x_offset, 8, 38])
        scale([1, 1.5, 0.5])
        sphere(r=3);

        // White highlight
        color("white")
        translate([x_offset+1, 9, 39.5])
        sphere(r=1);
    }

    eye(-4);
    eye(4);

    // Mouth - small beak-like shape
    color("darkred")
    translate([0, 6, 32])
    rotate([90, 0, 0])
    cylinder(h=3, r1=2, r2=1);

    // Arms - short stubby arms
    module arm(x_offset) {
        color("dodgerblue")
        translate([x_offset, 12, 25])
        rotate([0, 0, x_offset > 0 ? -20 : 20])
        scale([0.8, 0.8, 1.2])
        sphere(r=6);
    }

    arm(-12);
    arm(12);

    // Legs - simple cylinders
    module leg(x_offset) {
        color("dodgerblue")
        translate([x_offset, 0, 8])
        cylinder(h=15, r1=4, r2=3);

        // Feet
        color("dodgerblue")
        translate([x_offset, 0, 0])
        rotate([0, 90, 0])
        scale([1.5, 1, 0.8])
        cylinder(h=8, r1=3, r2=3);
    }

    leg(-7);
    leg(7);

    // Small leaves at base
    module leaf(angle) {
        color("forestgreen")
        rotate([0, 0, angle])
        translate([0, 15, 10])
        rotate([90, 0, 30])
        scale([0.3, 1, 0.1])
        cylinder(h=12, r1=4, r2=2);
