    translate([0, 0, 2]) scale([1.3, 1.1, 0.8]) sphere(6);
}

// Leg module
module leg(length, angle) {
    rotate([0, 0, angle]) {
        // Upper leg
        color(brown) translate([0, 0, 6]) rotate([90, 0, 0]) cylinder(h=length*0.4, r=2, $fn=8);
        // Lower leg
        color(brown) translate([0, length*0.4, 2]) rotate([90, 0, 0]) cylinder(h=length*0.6, r=1.5, $fn=8);
