                    sphere(r=8, $fn=32);
                }
            }
        }

        // Wings (4 wings total)
        color(wing_color) {
            // Upper wings
            for(side = [-1, 1]) {
                translate([side * 15, 0, 12]) {
                    rotate([0, 0, side * 20]) {
                        rotate([0, 90, 0]) {
                            scale([1, 2.5, 0.1]) {
                                cylinder(h=20, r=8, $fn=32);
                            }
                        }
                    }
                }
            }

            // Lower wings
            for(side = [-1, 1]) {
                translate([side * 12, -5, 8]) {
                    rotate([0, 0, side * 15]) {
                        rotate([0, 90, 0]) {
                            scale([0.8, 2, 0.08]) {
                                cylinder(h=18, r=7, $fn=32);
                            }
                        }
                    }
                }
            }
        }

        // Wing patterns (dots)
        color(accent_color) {
            for(side = [-1, 1]) {
                translate([side * 15, 5, 12]) {
                    rotate([0, 0, side * 20]) {
                        for(i = [0:3]) {
                            for(j = [0:2]) {
                                translate([side * (i*3 - 4), j*4 - 4, 0.5]) {
                                    sphere(r=0.8, $fn=16);
                                }
                            }
                        }
                    }
                }
            }
        }

        // Antennae
        color(body_color) {
            for(side = [-1, 1]) {
                translate([side * 5, 18, 12]) {
                    rotate([30, 0, side * 20]) {
                        cylinder(h=15, r=0.8, $fn=16);
                    }
                    translate([0, 15, 0]) {
                        sphere(r=1.5, $fn=16);
                    }
                }
            }
        }

        // Eyes
        color(eye_color) {
            for(side = [-1, 1]) {
                translate([side * 4, 16, 10]) {
                    scale([1.2, 1, 0.5]) {
                        sphere(r=3, $fn=16);
                    }
                }
            }
        }

        // Legs (6 legs)
        color(body_color) {
            for(i = [0:2]) {
                angle = i * 120;
                for(side = [-1, 1]) {
                    rotate([0, 0, angle]) {
                        translate([side * 8, 5 - i*5, 0]) {
                            rotate([60, 0, 0]) {
                                cylinder(h=12, r=1, $fn=16);
