        // Body markings
        color(accent_color) {
            translate([0, 8, 14]) {
                scale([2, 1.5, 0.2]) {
                    sphere(r=3, $fn=16);
                }
            }

            translate([0, -5, 10]) {
                scale([1.5, 2, 0.2]) {
                    sphere(r=2.5, $fn=16);
                }
            }
        }
    }
}

