// Poliwhirl OpenSCAD Model - Medium Complexity Realistic Style
// Scale: 50mm height

$fn = 50;

// Colors
color_body = [0.0, 0.6, 0.9];
color_belly = [1.0, 1.0, 1.0];
color_swirl = [0.4, 0.2, 0.0];
color_mouth = [0.8, 0.2, 0.2];

module poliwhirl_body() {
    // Main spherical body
    color(color_body)
    sphere(d=45);

    // White belly
    color(color_belly)
    translate([0, -22.5, 0])
    sphere(d=45);

    // Flatten slightly for 3D printing
    difference() {
        sphere(d=45);
        translate([0, 0, -25])
        cube([100, 100, 50], center=true);
    }
}

module poliwhirl_arms() {
    // Left arm
    color(color_body)
    translate([-25, 0, 5])
    rotate([0, 0, 45])
    scale([1, 1.5, 1])
    sphere(d=15);

    // Right arm
    color(color_body)
    translate([25, 0, 5])
    rotate([0, 0, -45])
    scale([1, 1.5, 1])
    sphere(d=15);
}

module poliwhirl_legs() {
    // Left leg
    color(color_body)
    translate([-12, -15, -20])
    rotate([15, 0, 0])
    cylinder(h=20, d1=12, d2=8);

    // Right leg
    color(color_body)
    translate([12, -15, -20])
    rotate([15, 0, 0])
    cylinder(h=20, d1=12, d2=8);
}

module poliwhirl_ears() {
    // Left ear
    color(color_body)
    translate([-18, 0, 20])
    rotate([0, 0, -30])
    scale([0.8, 2, 0.5])
    sphere(d=10);

    // Right ear
    color(color_body)
    translate([18, 0, 20])
    rotate([0, 0, 30])
    scale([0.8, 2, 0.5])
    sphere(d=10);
}

module poliwhirl_face() {
    // Eyes
    for(x=[-8, 8]) {
        color([0, 0, 0])
        translate([x, 8, 10])
        sphere(d=4);
    }

    // Mouth
    color(color_mouth)
    translate([0, 0, 5])
    rotate([90, 0, 0])
    linear_extrude(height=2)
    circle(d=8);
}

module poliwhirl_swirl() {
    // Spiral on belly
    color(color_swirl)
    for(i=[0:10:360]) {
        translate([
            cos(i) * (8 - i/45),
            -22.5 + sin(i) * (8 - i/45),
            0.1
        ])
        sphere(d=3 - i/120);
    }
}

// Assemble the complete Poliwhirl
union() {
    poliwhirl_body();
    poliwhirl_arms();
    poliwhirl_legs();
    poliwhirl_ears();
    poliwhirl_face();
    poliwhirl_swirl();
}

