// Abra Pokemon in realistic style
// Size: ~80mm tall, 3D printable

$fn = 32;

module abra() {
    color("tan") {
        // Main body
        translate([0, 0, 40]) {
            difference() {
                sphere(d = 35);
                // Neck cavity
                translate([0, 0, -10])
                    cylinder(h = 20, d1 = 25, d2 = 20, center = true);
            }
        }

        // Torso
        translate([0, 0, 15]) {
            scale([1.2, 1, 1.5])
                sphere(d = 30);
        }

        // Legs
        for(x = [-8, 8]) {
            translate([x, 0, 5]) {
                rotate([0, -10, 0])
                    cylinder(h = 15, d1 = 12, d2 = 10);
            }
        }

        // Feet
        for(x = [-8, 8]) {
            translate([x, 0, 0]) {
                scale([1.5, 2.5, 0.8])
                    sphere(d = 10);
            }
        }

        // Arms
        for(x = [-15, 15]) {
            translate([x, 0, 25]) {
                rotate([0, 0, x > 0 ? -20 : 20])
                    cylinder(h = 20, d1 = 8, d2 = 6);
            }
        }

        // Hands with three fingers
        for(x = [-15, 15]) {
            translate([x + (x > 0 ? -3 : 3), 0, 15]) {
                // Palm
                sphere(d = 8);

                // Fingers
                for(angle = [0, 120, 240]) {
                    rotate([0, 0, angle + (x > 0 ? 0 : 180)])
                        translate([4, 0, 0])
                            cylinder(h = 6, d1 = 3, d2 = 2);
                }
            }
        }
    }

    // Head
    color("bisque") {
        translate([0, 0, 60]) {
            sphere(d = 25);
        }

        // Snout
        color("tan") {
            translate([0, -10, 60]) {
                scale([0.8, 1.2, 0.8])
                    sphere(d = 15);
            }
        }
    }

    // Ears - long and pointed
    color("tan") {
        for(x = [-12, 12]) {
            translate([x, 0, 72]) {
                rotate([0, 0, x > 0 ? -10 : 10])
                    scale([0.3, 1, 0.2])
                        cylinder(h = 20, d1 = 5, d2 = 2);
            }
        }
    }

    // Mustache-like whiskers
    color("brown") {
        for(side = [-1, 1]) {
            for(i = [0, 1, 2]) {
                translate([side * (8 + i * 2), -15, 58]) {
                    rotate([0, 10 + i * 5, side * (30 + i * 10)])
                        cylinder(h = 8, d = 2);
                }
            }
        }
    }

    // Eyes - closed
    color("slateblue") {
        for(x = [-6, 6]) {
            translate([x, -5, 62]) {
                scale([1, 0.2, 1])
                    sphere(d = 5);
            }
        }
    }

    // Tail - thick at base, tapering
    color("tan") {
        translate([0, 20, 25]) {
            rotate([0, -30, 0])
                cylinder(h = 30, d1 = 12, d2 = 5);
        }
    }

    // Chest armor plating
    color("yellow") {
        translate([0, 0, 35]) {
            difference() {
                scale([1.3, 1, 0.5])
                    sphere(d = 25);
                translate([0, 0, -1])
                    cube([30, 30, 2], center = true);
            }
        }
    }

    // Knee pads
    color("yellow") {
        for(x = [-8, 8]) {
            translate([x, 0, 12]) {
                sphere(d = 8);
            }
        }
    }

    // Shoulder guards
    color("yellow") {
        for(x = [-15, 15]) {
            translate([x, 0, 28]) {
                sphere(d = 6);
            }
        }
    }

    // Spoons (Abra's signature items)
    color("silver") {
        for(side = [-1, 1]) {
            translate([side * 18, side * 5, 15]) {
                rotate([0, 0, side * 45])
                    scale([0.1, 2, 0.1])
                        cube([5, 30, 5], center = true);
            }
        }
    }
}

