    color(color_yellow) {
        union() {
            // Torso
            translate([0, 0, 25])
                scale([1.2, 1.5, 1.8])
                sphere(r=15, $fn=32);

            // Lower body
            translate([0, 0, 10])
                cylinder(h=20, r1=12, r2=15, $fn=32);
        }
    }

    // Head
    color(color_yellow) {
        translate([0, 0, 45])
            sphere(r=18, $fn=32);
    }

    // Mustache
    color(color_brown) {
        for(i=[-1,1]) {
            translate([i*8, 20, 42])
                rotate([0, i*20, 0])
                scale([1.5, 0.5, 4])
                sphere(r=5, $fn=16);
        }
    }

    // Eyes
    color([0,0,0]) {
        for(i=[-1,1]) {
            translate([i*8, 18, 48])
                sphere(r=3, $fn=16);
        }
    }

    // Ears
    color(color_yellow) {
        for(i=[-1,1]) {
            translate([i*25, 10, 55])
                rotate([0, -30*i, 0])
                scale([3, 1, 8])
                cylinder(h=25, r=3, $fn=16);
        }
    }

    // Spoons
    color(color_orange) {
        // Left spoon
        translate([-30, 10, 35])
            rotate([0, 0, -45])
            cylinder(h=50, r=2, $fn=16);
        translate([-55, -5, 35])
            rotate([0, 0, -45])
            scale([1, 0.2, 5])
            sphere(r=4, $fn=16);

        // Right spoon
        translate([30, 10, 35])
            rotate([0, 0, 45])
            cylinder(h=50, r=2, $fn=16);
        translate([55, -5, 35])
            rotate([0, 0, 45])
            scale([1, 0.2, 5])
            sphere(r=4, $fn=16);
    }

    // Arms
    color(color_yellow) {
        for(i=[-1,1]) {
            translate([i*20, 0, 35])
                rotate([0, 0, i*30])
                cylinder(h=25, r=5, $fn=16);
        }
    }

    // Legs
    color(color_yellow) {
        for(i=[-1,1]) {
            translate([i*8, 0, 5])
                cylinder(h=15, r=6, $fn=16);
        }
    }

    // Feet
    color(color_yellow) {
        for(i=[-1,1]) {
            translate([i*8, 0, 0])
                scale([2, 3, 1])
                sphere(r=5, $fn=16);
        }
    }

    // Tail
    color(color_yellow) {
        translate([0, -20, 20])
            rotate([90, 0, 0])
            scale([1, 1, 0.5])
            cylinder(h=30, r1=8, r2=3, $fn=16);
    }

    // Star on forehead
    color(color_brown) {
        translate([0, 15, 58])
            linear_extrude(height=2)
            polygon(points=[[0,0], [3,8], [8,8], [4,12], [5,18], [0,15], [-5,18], [-4,12], [-8,8], [-3,8]]);
    }
}

