// Alakazam OpenSCAD Model - Medium Complexity
// Realistic style, 3D printable, 100mm height

// Main colors
color_yellow = [1, 0.8, 0.2];
color_brown = [0.4, 0.2, 0.1];
color_skin = [0.9, 0.7, 0.5];
color_red = [0.8, 0.2, 0.2];

// Overall scale
scale_factor = 1;

module head() {
    // Main head sphere
    color(color_yellow)
    translate([0, 0, 85])
    scale([0.8, 0.7, 0.9])
    sphere(10);

    // Mustache
    for(i = [-1, 1]) {
        color(color_brown)
        translate([i * 6, 8, 83])
        rotate([0, 0, i * 30])
        scale([0.3, 1.5, 0.3])
        cylinder(h=8, r1=2, r2=0.5);
    }

    // Eyes
    for(i = [-1, 1]) {
        color(color_red)
        translate([i * 3, 4, 88])
        scale([0.8, 1.2, 0.5])
        sphere(2);

        color([0, 0, 0])
        translate([i * 3, 4.5, 89])
        sphere(0.8);
    }

    // Nose
    color(color_skin)
    translate([0, 7, 85])
    sphere(1.5);

    // Mouth
    color([0, 0, 0])
    translate([0, 9, 83])
    scale([2, 0.8, 0.5])
    sphere(1);
}

module ears() {
    for(side = [-1, 1]) {
        color(color_yellow)
        translate([side * 12, 0, 92])
        rotate([0, 0, side * 15])
        rotate([30, 0, 0])
        scale([0.8, 0.3, 2])
        cylinder(h=8, r1=3, r2=1);

        // Inner ear
        color(color_skin)
        translate([side * 12, 0.5, 93])
        rotate([0, 0, side * 15])
        rotate([30, 0, 0])
        scale([0.6, 0.2, 1.8])
        cylinder(h=7, r1=2, r2=0.5);
    }
}

module body() {
    // Main torso
    color(color_yellow)
    translate([0, 0, 50])
    scale([1, 0.8, 1.2])
    cylinder(h=25, r1=12, r2=8);

    // Chest armor
    color(color_brown)
    translate([0, -5, 45])
    scale([1.2, 0.5, 0.8])
    sphere(10);

    // Belt
    color(color_brown)
    translate([0, -10, 45])
    scale([1.3, 0.3, 0.3])
    cylinder(h=25, r=10);
}

module arms() {
    for(side = [-1, 1]) {
        // Upper arm
        color(color_yellow)
        translate([side * 15, 0, 65])
        rotate([0, 0, side * -20])
        cylinder(h=10, r=3);

        // Forearm
        translate([side * 22, -2, 60])
        rotate([0, 0, side * -45])
        cylinder(h=12, r=2.5);

        // Hand
        translate([side * 28, -4, 55])
        sphere(3);

        // Three fingers
        for(f = [0, 1, 2]) {
            translate([side * (28 + f * 1.5), -6, 55])
            scale([0.4, 1, 0.4])
            cylinder(h=3, r=1);
        }
    }
}

module legs() {
    for(side = [-1, 1]) {
        // Upper leg
        color(color_yellow)
        translate([side * 8, 0, 30])
        rotate([0, 0, side * 10])
        cylinder(h=15, r=3);

        // Lower leg
        translate([side * 10, 0, 15])
        rotate([0, 0, side * 5])
        cylinder(h=12, r=2.5);

        // Foot
        translate([side * 11, 2, 5])
        scale([1.2, 2, 0.8])
        sphere(3);
    }
}

module spoons() {
    for(side = [-1, 1]) {
        // Spoon handle
        color([0.7, 0.7, 0.7])
        translate([side * 35, -10, 60])
        rotate([0, 0, side * 45])
        rotate([90, 0, 0])
        cylinder(h=25, r=1);

        // Spoon bowl
        translate([side * 40, -15, 55])
        rotate([0, 90, 0])
        scale([1, 1, 0.3])
        sphere(4);
    }
}

// Assembly
scale([scale_factor, scale_factor, scale_factor]) {
    head();
    ears();
    body();
    arms();
    legs();
    spoons();
}

