Color_Yellow = [0.9, 0.8, 0.2];
Color_Red = [0.8, 0.2, 0.2];
Color_White = [0.9, 0.9, 0.9];

module victreebel() {
    // Main bell-shaped body
    color(Color_Green) {
        // Base bell shape using sphere and cylinder
        translate([0, 0, 0]) {
            difference() {
                union() {
                    cylinder(h = 40, r1 = 25, r2 = 20, center = false);
                    translate([0, 0, 40]) sphere(d = 50);
                }
                // Hollow out the inside
                translate([0, 0, -1]) cylinder(h = 42, r1 = 22, r2 = 17, center = false);
                translate([0, 0, 40]) sphere(d = 47);
            }
        }

        // Leaf collar around neck
        for(i = [0:7]) {
            rotate([0, 0, i * 45]) {
                translate([0, 22, 35]) {
                    rotate([0, 90, 0]) {
                        scale([1, 0.3, 0.5]) {
                            color(Color_LightGreen) sphere(d = 15);
                        }
                    }
                }
            }
        }
    }

    // Mouth opening
    color(Color_Red) {
        translate([0, 0, 60]) {
            difference() {
                sphere(d = 40);
                translate([0, 0, -5]) sphere(d = 38);
                // Teeth indentations
                for(i = [0:3]) {
                    rotate([0, 0, i * 90 + 45]) {
                        translate([15, 0, 0]) cube([8, 2, 10], center = true);
                    }
                }
            }
        }
    }

    // Eyes
    color(Color_Red) {
        translate([-12, 20, 55]) sphere(d = 8);
        translate([12, 20, 55]) sphere(d = 8);
    }

    // Eye pupils
    color(Color_White) {
        translate([-12, 22, 57]) sphere(d = 3);
        translate([12, 22, 57]) sphere(d = 3);
    }

    // Leaf on back
    color(Color_LightGreen) {
        translate([0, -30, 35]) {
            rotate([0, -20, 0]) {
                scale([1, 0.2, 2]) {
                    sphere(d = 20);
                }
                // Leaf veins
                for(i = [-1:1]) {
                    translate([i * 5, 0, 0]) {
                        scale([0.1, 0.1, 1]) {
                            cylinder(h = 35, r1 = 2, r2 = 2);
                        }
                    }
                }
            }
        }
    }

    // Tentacle-like vine
    color(Color_Green) {
        translate([0, 0, 0]) {
            rotate([90, 0, 0]) {
                for(i = [0:4]) {
                    translate([0, 0, -i * 5]) {
                        rotate([0, 0, i * 15]) {
                            cylinder(h = 5, r1 = 3 - i * 0.3, r2 = 3 - (i + 1) * 0.3);
                        }
                    }
                }
            }
        }
    }

    // Spots on body
    color(Color_Yellow) {
        for(pos = [[-15, 10, 30], [15, -10, 35], [-10, -15, 45]]) {
            translate(pos) sphere(d = 4);
        }
    }
}

