// Magnemite in realistic style - medium complexity
// Overall height ~80mm, 3D printable

$fa = 2;
$fs = 0.5;

module body_sphere() {
    color("Silver") 
    difference() {
        sphere(d = 40);

        // Eye sockets
        translate([-12, 12, 12]) rotate([0, 0, 0]) sphere(d = 12);
        translate([12, 12, 12]) rotate([0, 0, 0]) sphere(d = 12);

        // Bottom flat
        translate([0, 0, -20]) cube([50, 50, 20], center = true);
    }
}

module eye() {
    color("Red")
    union() {
        // Iris
        sphere(d = 8);
        // Pupil
        color("Black")
        translate([0, 0, 3])
        sphere(d = 4);
    }
}

module magnet() {
    color("Silver")
    union() {
        // Cylinder body
        cylinder(h = 25, d1 = 15, d2 = 18);

        // Top cap
        translate([0, 0, 25])
        cylinder(h = 3, d = 20);

        // Bottom cap
        translate([0, 0, 0])
        cylinder(h = 3, d = 20);

        // Side screws
        for(i = [0:120:359]) {
            rotate([0, 0, i])
            translate([9, 0, 12.5])
            cylinder(h = 8, d = 3);
        }
    }
}

module screw() {
    color("Silver")
    rotate([90, 0, 0])
    cylinder(h = 8, d = 5);
}

module eyebrow() {
    color("Gray")
    rotate([15, 0, 0])
    scale([1, 0.3, 1])
    sphere(d = 15);
}

module main_body() {
    union() {
        body_sphere();

        // Left eye
        translate([-12, 12, 12])
        eye();

        // Right eye
        translate([12, 12, 12])
        eye();

        // Eyebrows
        translate([-12, 20, 18])
        eyebrow();
        translate([12, 20, 18])
        mirror([1, 0, 0]) eyebrow();

        // Side screws
        translate([0, 20, 0]) screw();
        translate([0, -20, 0]) screw();
        translate([20, 0, 0]) screw();
        translate([-20, 0, 0]) screw();
    }
}

module left_magnet() {
    translate([-30, 0, 0])
    rotate([90, 0, 0])
    magnet();
}

module right_magnet() {
    translate([30, 0, 0])
    rotate([90, 0, 0])
    magnet();
}

module top_magnet() {
    translate([0, 0, 30])
    rotate([0, 0, 0])
    magnet();
}

// Main assembly
union() {
    main_body();
    left_magnet();
    right_magnet();
    top_magnet();
}

// Realistic Magneton OpenSCAD Model
// Medium complexity, 3D printable

$fn = 32;

// Main dimensions
magnet_radius = 12;
magnet_thickness = 5;
eye_radius = 2.5;
screw_radius = 1.5;
body_gap = 2;
support_thickness = 3;

// Color definitions
silver = [0.75, 0.75, 0.8];
red = [0.8, 0.2, 0.2];
black = [0.1, 0.1, 0.1];
yellow = [0.9, 0.8, 0.2];

// Module for single magneton disk
module magneton_disk(x_pos, y_pos, z_pos) {
    translate([x_pos, y_pos, z_pos]) {
        // Main disk body
        color(silver)
        difference() {
            cylinder(h = magnet_thickness, r = magnet_radius);
            // Center hole for screw
            cylinder(h = magnet_thickness + 0.1, r = screw_radius);
        }

        // Eyes
        translate([magnet_radius * 0.5, magnet_radius * 0.3, magnet_thickness])
        color(red)
        sphere(r = eye_radius);

        translate([magnet_radius * 0.5, -magnet_radius * 0.3, magnet_thickness])
        color(red)
        sphere(r = eye_radius);

        // Eye pupils
        translate([magnet_radius * 0.5 + 0.7, magnet_radius * 0.3, magnet_thickness + eye_radius - 0.5])
        color(black)
        sphere(r = 0.8);

        translate([magnet_radius * 0.5 + 0.7, -magnet_radius * 0.3, magnet_thickness + eye_radius - 0.5])
        color(black)
        sphere(r = 0.8);

        // Side magnets
        for (i = [0:2]) {
            rotate([0, 0, i * 120])
            translate([magnet_radius + 3, 0, magnet_thickness/2])
            color(silver)
            cylinder(h = 8, r = 2.5);
        }

        // Top screw head
        translate([0, 0, magnet_thickness])
        color(yellow)
        cylinder(h = 2, r = 3);
    }
}

// Module for support structure between disks
module support() {
    color(silver)
    difference() {
        // Main support ring
        cylinder(h = support_thickness, r = magnet_radius + 5);
        // Hollow center
        cylinder(h = support_thickness + 0.1, r = magnet_radius - 2);
    }
}

// Main assembly
union() {
    // First disk (top)
    magneton_disk(0, 0, 0);

    // Support structure
    translate([0, 0, magnet_thickness])
    support();

    // Second disk (middle)
    translate([0, 0, magnet_thickness + support_thickness])
    magneton_disk(0, 0, 0);

    // Second support
    translate([0, 0, magnet_thickness * 2 + support_thickness])
    support();

    // Third disk (bottom)
    translate([0, 0, magnet_thickness * 2 + support_thickness * 2])
    magneton_disk(0, 0, 0);

    // Side magnetic connections
    translate([magnet_radius + 8, 0, magnet_thickness + support_thickness/2])
    color(silver)
    rotate([0, 90, 0])
    cylinder(h = 10, r = 3);

    translate([-magnet_radius - 8, 0, magnet_thickness + support_thickness/2])
    color(silver)
    rotate([0, 90, 0])
    cylinder(h = 10, r = 3);

    // Diagonal support arms
    translate([magnet_radius + 5, magnet_radius + 5, magnet_thickness * 2 + support_thickness * 2])
    color(silver)
    rotate([0, 45, 0])
    cylinder(h = 15, r = 2);

    translate([-magnet_radius - 5, -magnet_radius - 5, magnet_thickness * 2 + support_thickness * 2])
    color(silver)
    rotate([0, 45, 0])
    cylinder(h = 15, r = 2);
}

// Farfetch'd Pokemon - Realistic Style (Medium Complexity)
