// Main module
module doduo() {
    union() {
        // First head
        translate([-15, 0, 30]) head(1);

        // Second head
        translate([15, 0, 30]) head(2);

        // Shared body
        body();

        // Legs
        translate([-10, 0, 0]) leg();
        translate([10, 0, 0]) leg();

        // Tail
        translate([0, -20, 5]) tail();
    }
}

// Head module
module head(id) {
    // Head base
    color(brown) {
        sphere(12);

        // Beak
        translate([0, 0, -12]) {
            // Upper beak
            rotate([id == 1 ? -20 : 20, 0, 0]) {
                scale([1, 0.8, 1.5]) sphere(8);
            }

            // Lower beak
            rotate([id == 1 ? 20 : -20, 0, 0]) {
                translate([0, 0, -8]) {
                    scale([0.9, 0.7, 1.2]) sphere(7);
                }
            }
        }
    }

    // Eyes
    color(white) {
        translate([id == 1 ? -6 : 6, 5, 5]) {
            sphere(3);
        }
    }

    color(black) {
        translate([id == 1 ? -6 : 6, 5, 6]) {
            sphere(1.5);
        }
    }

    // Eye brow ridge
    color(black) {
        translate([id == 1 ? -6 : 6, 4, 8]) {
            rotate([90, 0, id == 1 ? 20 : -20]) {
                scale([0.3, 1.5, 0.3]) cylinder(4, 1, 1);
            }
        }
    }

    // Neck
    color(brown) {
        translate([0, 0, -15]) {
            scale([0.6, 0.6, 2]) sphere(10);
        }
    }
}

// Body module
module body() {
    color(brown) {
        union() {
            // Main body
            scale([1.2, 1.5, 1]) sphere(20);

            // Chest
            translate([0, 10, -5]) {
                scale([0.8, 0.5, 0.8]) sphere(15);
            }

            // Back hump
            translate([0, -10, 5]) {
                scale([1, 0.8, 0.6]) sphere(18);
            }
        }
    }

    // Belly patch
    color(tan) {
        translate([0, 15, -15]) {
            scale([0.6, 0.4, 0.3]) sphere(20);
        }
    }
}

// Leg module
module leg() {
    color(brown) {
        // Upper leg
        translate([0, 0, 0]) {
            rotate([0, 0, 10]) {
                scale([0.4, 0.4, 1.2]) cylinder(15, 5, 4);
            }
        }

        // Lower leg
        translate([0, 0, -15]) {
            rotate([0, 0, 5]) {
                scale([0.3, 0.3, 1]) cylinder(12, 4, 3);
            }
        }

        // Foot
        translate([0, 5, -27]) {
            scale([1.2, 2, 0.6]) sphere(5);
        }

        // Toes
        for(i = [-1, 0, 1]) {
            translate([i * 3, 8, -27]) {
                scale([0.8, 0.5, 0.5]) sphere(3);
            }
        }
    }
}

// Tail module
module tail() {
    color(brown) {
        // Tail base
        translate([0, 0, 0]) {
            rotate([20, 0, 0]) {
                scale([0.8, 0.8, 1.5]) sphere(8);
            }
        }

        // Tail feathers
        for(i = [-1, 0, 1]) {
            color(i == 0 ? brown : tan) {
                translate([i * 3, -5, 2]) {
                    rotate([45, i * 10, 0]) {
                        scale([0.3, 1.5, 0.1]) cube([3, 12, 1]);
                    }
                }
            }
        }
    }
}

