    color(seel_white) {
        translate([30, side*12, 22]) rotate([0, -20, side*10]) {
            // Main ear
            scale([0.8, 0.2, 1.2]) sphere(d=15);
            // Inner ear
            color(seel_light_blue) 
                translate([0, -1, 0]) scale([0.7, 0.5, 0.9])
                sphere(d=12);
        }
    }
}

// Eye module
module seel_eye(side) {
    translate([45, side*8, 18]) {
        // White
        color(seel_white) sphere(d=8);
        // Pupil
        color(seel_black) translate([2, 0, 0]) sphere(d=5);
        // Highlight
        color(white) translate([3, 1.5, 2]) sphere(d=2);
    }
}

// Nose module
module seel_nose() {
    color(seel_tan) {
        // Nose tip
        translate([55, 0, 12]) rotate([0, -30, 0])
            scale([0.8, 0.6, 0.6]) sphere(d=8);
        // Nostrils
        color(seel_black) {
            translate([57, 2, 12]) rotate([0, -30, 0]) sphere(d=2);
            translate([57, -2, 12]) rotate([0, -30, 0]) sphere(d=2);
        }
    }
}

// Tail module
module seel_tail() {
    color(seel_white) {
        // Tail segments
        for(i=[0:3]) {
            translate([-30-i*8, 0, -8-i*3]) 
                rotate([0, i*10, 0])
                scale([0.8-i*0.1, 0.6-i*0.05, 0.4-i*0.05])
                sphere(d=20-i*3);
        }

        // Tail flippers
        color(seel_light_blue) {
            translate([-45, 0, -15]) rotate([0, 30, 0]) {
                // Left flipper
                translate([0, 8, 0]) rotate([0, 0, -20])
                    scale([0.1, 1.2, 0.8]) sphere(d=15);
                // Right flipper
                translate([0, -8, 0]) rotate([0, 0, 20])
                    scale([0.1, 1.2, 0.8]) sphere(d=15);
            }
        }
    }
}

// Fin modules
module seel_fin(pos, rot) {
    color(seel_light_blue) {
        translate(pos) rotate(rot) {
            // Main fin
            scale([1.5, 0.1, 0.8]) sphere(d=15);
            // Fin details
            for(i=[-1:1]) {
                translate([0, 0, i*4]) 
                    scale([0.8, 0.1, 0.2])
                    sphere(d=10);
            }
        }
    }
}

// Body markings
module seel_markings() {
    color(seel_blue) {
        // Side markings
        for(side=[-1,1]) {
            translate([10, side*15, 0]) {
                // Top marking
                rotate([90, 0, 0]) 
                    scale([1, 0.3, 1]) cylinder(h=3, d=20);
                // Bottom marking
                translate([0, 0, -10])
                    rotate([90, 0, 0]) 
                    scale([0.8, 0.3, 0.8]) cylinder(h=3, d=15);
            }
        }
    }
}

// Assembly
union() {
    // Main body parts
    seel_body();
    seel_head();
    seel_horn();
    seel_tail();

    // Symmetrical features
    for(side=[-1,1]) {
        seel_ear(side);
        seel_eye(side);
    }

    // Single features
    seel_nose();

    // Fins
    seel_fin([0, 0, -5], [0, 0, 0]); // Dorsal fin
    seel_fin([15, 20, -8], [90, 0, 0]); // Right side fin
    seel_fin([15, -20, -8], [90, 0, 0]); // Left side fin

    // Markings
    seel_markings();
}

