}

module grimer_face() {
    // Eyes - yellow with black pupils
    color([1, 0.8, 0]) 
    translate([-8, 15, 12])
        sphere(r = 4, $fn = 16);

    translate([8, 15, 12])
        sphere(r = 4, $fn = 16);

