    union() {
        translate([11, 16, 7])
            sphere(d=2);
        translate([-9, 16, 7])
            sphere(d=2);
    }

    // Shell edge detail
    color(shell_color)
    difference() {
        translate([0, 0, 15])
            cylinder(h=5, d=52);
        translate([0, 0, 15])
            cylinder(h=6, d=48);
    }

    // Small spikes around edge
    for(i = [0:11]) {
        rotate([0, 0, i*30])
            translate([24, 0, 15])
                cylinder(h=3, d1=4, d2=2);
    }
}

