    for(i=[0:60:300]) {
        rotate([0, 0, i])
            translate([25, 20, 0])
                spike(0, 0, 0, 0, 30);
    }

    // Bottom spikes
    for(i=[0:72:288]) {
        rotate([0, 0, i])
            translate([20, 0, -25])
                spike(0, 0, 0, 20, 25);
    }
