            cylinder(h = 12, r = 3.5);

            // Feet
            translate([side * 8, -20, 2])
            scale([1.3, 1.8, 0.5])
            sphere(r = 3.5);
        }
    }

    // Tail
    color(body_color)
    union() {
        translate([0, -25, 15])
        rotate([20, 0, 0])
        cylinder(h = 20, r1 = 5, r2 = 3);

        translate([0, -40, 10])
        scale([1.2, 0.8, 0.8])
        sphere(r = 4);
    }
}

// Main assembly
scale([0.8, 0.8, 0.8])
kingler();

// Voltorb Pokemon - Realistic Style, Medium Complexity
// Size: 60mm diameter, 3D printable with 2mm wall thickness

// Main color palette
$fa = 2;
$fs = 0.5;

// Main body - slightly flattened sphere for realistic look
module voltorb_body() {
    color([0.9, 0.1, 0.1])  // Deep red
    difference() {
        scale([1, 1, 0.9])
        sphere(d = 60);

        // Hollow interior for 3D printing
        scale([0.93, 0.93, 0.83])
        sphere(d = 60);
    }
}

// Eye sockets for realistic depth
module eye_socket(pos_x, pos_y) {
    translate([pos_x, pos_y, 25])
    rotate([0, 0, 0])
    color([0.05, 0.05, 0.05])
    cylinder(h = 5, d1 = 12, d2 = 10, $fn = 16);
}

// Eye whites
module eye_white(pos_x, pos_y) {
    translate([pos_x, pos_y, 21])
    color([0.95, 0.95, 0.95])
    sphere(d = 9);
}

// Pupils with realistic iris pattern
module pupil(pos_x, pos_y) {
    translate([pos_x, pos_y, 20])
    color([0.1, 0.1, 0.1])
    sphere(d = 5);

    // Iris ring
    translate([pos_x, pos_y, 20.5])
    color([0.3, 0.1, 0.1])
    cylinder(h = 0.5, d1 = 7, d2 = 7, $fn = 32);
}

// Electric bolt marking on top
module electric_marking() {
    color([0.95, 0.95, 0.1])  // Bright yellow
    translate([0, 0, 28])
    linear_extrude(height = 1)
    polygon(points=[
        [0, 8],
        [-2, 4],
        [-1, 4],
        [-3, 0],
        [-1, 0],
        [-2, -4],
        [0, -4],
        [2, -2],
        [1, -2],
        [3, 2],
        [1, 2],
        [2, 6]
    ]);
}

// Surface texture pattern for realistic look
module surface_pattern() {
    for(i = [0:30:330]) {
        rotate([0, 0, i])
        translate([20, 0, 0])
        color([0.8, 0.05, 0.05])
        rotate([90, 0, 0])
        cylinder(h = 0.5, d = 3, $fn = 6);
    }

    for(i = [15:30:345]) {
        rotate([0, 0, i])
        translate([30, 0, 0])
        color([0.8, 0.05, 0.05])
        rotate([90, 0, 0])
        cylinder(h = 0.5, d = 2.5, $fn = 6);
    }
}

// Bottom flat area for stability
module bottom_flat() {
    color([0.9, 0.1, 0.1])
    translate([0, 0, -25])
    cylinder(h = 5, d = 40);
}

// Assembly
union() {
    voltorb_body();

    // Add bottom flat area
    bottom_flat();

    // Eyes
    eye_socket(-15, 10);
    eye_socket(15, 10);

    eye_white(-15, 10);
    eye_white(15, 10);

    pupil(-15, 10);
    pupil(15, 10);

    // Electric marking
    electric_marking();

    // Surface texture
    surface_pattern();
}

