// Exeggcute - Realistic Style, Medium Complexity
// Scale to ~80mm width

module exeggcute() {
    // Colors
    color_light_purple = [0.85, 0.75, 0.9];
    color_dark_purple = [0.45, 0.3, 0.6];
    color_yolk = [1, 0.85, 0.2];
    color_eye = [0, 0, 0];
    color_stem = [0.1, 0.5, 0.2];
    color_crack = [0.3, 0.2, 0.4];
    color_shadow = [0.2, 0.1, 0.3];

    // Stem module
    module stem() {
        color(color_stem) {
            // Main stem
            translate([0, 0, -5]) {
                cylinder(h=10, r1=2.5, r2=1.5);
            }
            // Leaves
            rotate([0, -30, 0]) {
                translate([0, 3, -2]) {
                    scale([1, 0.3, 1]) {
                        sphere(3);
                    }
                }
            }
            rotate([0, 30, 180]) {
                translate([0, 3, -2]) {
                    scale([1, 0.3, 1]) {
                        sphere(3);
                    }
                }
            }
        }
    }

    // Single egg module
    module egg(rotation=[0,0,0], translation=[0,0,0], crack_type=0) {
        color(color_light_purple) {
            // Main egg body
            translate(translation) {
                rotate(rotation) {
                    scale([1, 1.2, 1]) {
                        sphere(12);
                    }

                    // Crack details
                    color(color_crack) {
                        if (crack_type == 0) {
                            // Large crack
                            translate([0, 0, 9]) {
                                linear_extrude(height=1) {
                                    polygon(points=[[0,0], [3,2], [1,4], [-1,3], [-2,5], [-4,2], [-2,0]]);
                                }
                            }
                        } else if (crack_type == 1) {
                            // Small crack
                            translate([0, 2, 8]) {
                                rotate([0, 90, 0]) {
                                    cylinder(h=8, r=0.5);
                                }
                            }
                        } else {
                            // Multiple cracks
                            for (i = [0:4]) {
                                rotate([0, 0, i*72]) {
                                    translate([0, 8, 5]) {
                                        cube([1, 4, 0.5], center=true);
                                    }
                                }
                            }
                        }
                    }

                    // Eyes
                    color(color_eye) {
                        // Left eye
                        translate([-4, 5, 8]) {
                            scale([1, 1.5, 0.5]) {
                                sphere(2);
                            }
                        }
                        // Right eye
                        translate([4, 5, 8]) {
                            scale([1, 1.5, 0.5]) {
                                sphere(2);
                            }
                        }
                    }

                    // Yolk showing through crack
                    color(color_yolk) {
                        translate([0, 0, 8.5]) {
                            sphere(4);
                        }
                    }

                    // Purple spots
                    color(color_dark_purple) {
                        for (phi = [30:60:150]) {
                            for (theta = [0:45:315]) {
                                rotate([phi, 0, theta]) {
                                    translate([0, 0, 11.5]) {
                                        sphere(1);
                                    }
                                }
                            }
                        }
                    }
                }
            }
        }
    }

    // Create the cluster of 6 eggs
    union() {
        // Front center egg
        egg([0, 0, 0], [0, 0, 0], 0);

        // Top egg
        egg([0, -30, 0], [0, 15, 8], 1);

        // Bottom left egg
        egg([0, 30, -30], [-12, -8, -6], 2);

        // Bottom right egg
        egg([0, 30, 30], [12, -8, -6], 0);

        // Back left egg
        egg([30, 0, 45], [-8, -2, -12], 1);

        // Back right egg
        egg([-30, 0, -45], [8, -2, -12], 2);

        // Stems for bottom eggs
        translate([-12, -8, -16]) {
            rotate([0, 0, -30]) {
                stem();
            }
        }
        translate([12, -8, -16]) {
            rotate([0, 0, 30]) {
                stem();
            }
        }
    }
}

// Render the complete model
exeggcute();

// Exeggutor - Realistic Style, Medium Complexity
// OpenSCAD 3D printable model

// Color definitions
$fn = 32;
body_color = "#78C850";  // Grass green
coconut_color = "#8B4513";  // Saddle brown
face_color = "#F5DEB3";  // Wheat
eye_color = "#FF0000";  // Red
mouth_color = "#2F4F2F";  // Dark green
stalk_color = "#90EE90";  // Light green

// Main body - palm tree trunk
module trunk() {
    color(body_color) {
        translate([0, 0, 0])
            cylinder(h = 60, d1 = 18, d2 = 12);

        // Trunk segments for realistic texture
        for (i = [0:5:55]) {
            translate([0, 0, i])
                cylinder(h = 3, d1 = 16 - i/10, d2 = 15 - i/10);
        }
    }
}

// Coconut heads with faces
module coconut_head(x, y, z, rot_x, rot_y, rot_z) {
    translate([x, y, z]) {
        rotate([rot_x, rot_y, rot_z]) {
            color(coconut_color) {
                // Main coconut
                sphere(d = 22);

                // Face area
                color(face_color) {
                    translate([0, -10, 5])
                        sphere(d = 15);
                }

                // Eyes
                color(eye_color) {
                    translate([-5, -12, 8])
                        sphere(d = 4);
                    translate([5, -12, 8])
                        sphere(d = 4);
                }

                // Mouth
                color(mouth_color) {
                    translate([0, -8, 3])
                        rotate([90, 0, 0])
                        cylinder(h = 2, d1 = 6, d2 = 3);
                }

                // Coconut texture lines
                for (i = [0:30:360]) {
                    rotate([0, 0, i])
                        translate([11, 0, 0])
                            cube([1, 0.5, 22], center = true);
                }
            }
        }
    }
}

// Palm leaves
module palm_leaf(length, width, angle) {
    color(stalk_color) {
        rotate([0, 0, angle]) {
            // Main leaf blade
            translate([length/2, 0, 0])
                scale([length, width, 1])
                cube([1, 1, 0.5], center = true);

            // Leaf segments
            for (i = [0:5:length]) {
                translate([i, 0, 0.5])
                    rotate([0, 0, i/2])
                        cube([length/10, width/2, 0.3], center = true);
                translate([i, 0, -0.5])
                    rotate([0, 0, -i/2])
                        cube([length/10, width/2, 0.3], center = true);
            }
        }
    }
}

// Leaf crown assembly
module leaf_crown() {
    translate([0, 0, 70]) {
        // Central stalk
        color(stalk_color) {
            cylinder(h = 20, d = 4);
        }

        // Palm leaves in radial pattern
        for (i = [0:45:360]) {
            translate([0, 0, 20])
                rotate([30, 0, 0])
                    palm_leaf(35, 8, i);
        }
    }
}

// Feet for stability
module feet() {
    color(body_color) {
        translate([-8, -8, -2])
            cylinder(h = 4, d = 8);
        translate([8, -8, -2])
            cylinder(h = 4, d = 8);
        translate([0, 8, -2])
            cylinder(h = 4, d = 8);
    }
}

// Main assembly
union() {
    // Base and trunk
    feet();
    trunk();

    // Three coconut heads
    coconut_head(0, 15, 65, -20, 0, 0);
    coconut_head(-15, -5, 60, 10, -15, -30);
    coconut_head(15, -5, 60, 10, 15, 30);

    // Leaf crown
    leaf_crown();
}
