$fn = 32;

// Overall scale
scale(0.4) {

// Body
color([1, 0.8, 0]) {
    union() {
        // Torso
        translate([0, 0, 25])
            scale([1.2, 1, 1.4])
            sphere(20);

        // Belly
        color([1, 1, 0.85])
        translate([0, -19, 20])
            scale([1.1, 0.3, 1.2])
            sphere(18);
    }
}

// Head
color([1, 0.8, 0]) {
    translate([0, 0, 55])
        sphere(22);
}

// Ears
module ear(x) {
    color([1, 0.8, 0]) {
        translate([x, 0, 70])
            rotate([0, x*3, 0])
            scale([0.3, 0.1, 1.2])
            cylinder(20, 8, 3);
    }
    color([0.2, 0.2, 0.2]) {
        translate([x, 0, 85])
            rotate([0, x*3, 0])
            scale([0.25, 0.08, 0.7])
            cylinder(8, 6, 2);
    }
}
ear(12);
ear(-12);

// Eyes
module eye(x) {
    color([0.1, 0.1, 0.2])
    translate([x, -20, 55])
        scale([1, 1.2, 0.5])
        sphere(4);
    color([0.9, 0.9, 1])
    translate([x+1.5, -21, 56])
        sphere(1.5);
}
eye(8);
eye(-8);

// Cheeks
color([1, 0.2, 0.2]) {
    translate([13, -18, 50])
        sphere(5);
    translate([-13, -18, 50])
        sphere(5);
}

// Nose
color([0.2, 0.2, 0.2])
translate([0, -21, 52])
    scale([1.2, 0.8, 0.5])
    sphere(1.5);

// Mouth
color([0.3, 0.2, 0.2])
translate([0, -22, 48])
    rotate([90, 0, 0])
    cylinder(0.5, 5, 2, $fn=3);

// Arms
module arm(x) {
    color([1, 0.8, 0])
    translate([x, -15, 35])
        rotate([0, 30*x, 10])
        scale([0.4, 0.4, 1])
        cylinder(15, 5, 4);
}
arm(20);
arm(-20);

// Legs
module leg(x) {
    color([1, 0.8, 0])
    translate([x, 0, 0])
        scale([0.5, 0.5, 1])
        cylinder(20, 6, 4);
    color([0.8, 0.6, 0])
    translate([x, 0, 0])
        cylinder(2, 7, 7);
}
leg(8);
leg(-8);

// Tail
color([1, 0.8, 0]) {
    translate([0, 20, 20])
        rotate([0, -30, 0])
        scale([0.8, 0.3, 2])
        cylinder(25, 8, 4);
}
color([0.6, 0.4, 0]) {
    translate([0, 42, 55])
        rotate([0, -30, 0])
        scale([0.7, 0.25, 1.5])
        cylinder(15, 7, 3);
}

// Back stripes
color([0.6, 0.4, 0]) {
    for(i = [1:3]) {
        translate([0, 15 - i*5, 45 - i*8])
            rotate([90, 0, 0])
            scale([1.2, 0.2, 1])
            cylinder(0.5, 15, 15);
    }
}

}
