    color([1, 0.8, 0])
    translate([x, 0, 0])
        scale([0.5, 0.5, 1])
        cylinder(20, 6, 4);
    color([0.8, 0.6, 0])
    translate([x, 0, 0])
        cylinder(2, 7, 7);
}
leg(8);
leg(-8);

// Tail
