// Lugia - Realistic Style
// Scale: 80mm tall

// Main colors
color_body = [0.85, 0.87, 0.89]; // Light silver
color_accents = [0.05, 0.15, 0.35]; // Dark blue
color_eyes = [0.9, 0.1, 0.1]; // Red

// Body proportions
scale_factor = 80/100;

module head() {
    union() {
        // Main head shape
        translate([0, 0, 40*scale_factor])
            scale([1, 1.2, 1.3])
            sphere(15*scale_factor);

        // Snout
        translate([0, -18*scale_factor, 35*scale_factor])
            scale([0.8, 0.6, 0.8])
            sphere(12*scale_factor);

        // Back head extension
        translate([0, 12*scale_factor, 42*scale_factor])
            scale([0.6, 0.4, 0.5])
            sphere(10*scale_factor);
    }
}

module ears() {
    // Left ear
    translate([-12*scale_factor, 5*scale_factor, 52*scale_factor])
        rotate([0, -20, -30])
        scale([0.3, 2, 0.2])
        cylinder(h=20*scale_factor, r=5*scale_factor);

    // Right ear
    translate([12*scale_factor, 5*scale_factor, 52*scale_factor])
        rotate([0, -20, 30])
        scale([0.3, 2, 0.2])
        cylinder(h=20*scale_factor, r=5*scale_factor);
}

module eyes() {
    // Left eye
    translate([-8*scale_factor, -15*scale_factor, 43*scale_factor])
        rotate([0, 10, 0])
        scale([1, 0.5, 0.8])
        sphere(4*scale_factor);

    // Right eye
    translate([8*scale_factor, -15*scale_factor, 43*scale_factor])
        rotate([0, 10, 0])
        scale([1, 0.5, 0.8])
        sphere(4*scale_factor);
}

module neck() {
    translate([0, 5*scale_factor, 30*scale_factor])
        rotate([0, 0, 0])
