// Rayquaza - Medium complexity realistic style
// Overall size ~80mm

$fn = 32;
scale_factor = 1.5;

module body_segment(r, h, color_val) {
    color(color_val)
    difference() {
        union() {
            cylinder(h=h, r1=r, r2=r*0.9);
            translate([0, 0, h/2])
                sphere(r=r*1.1);
        }
        translate([0, -r-1, h/2])
            cube([r*2, r*2, h*2], center=true);
    }
}

module fin(base_w, base_h, length, thickness, color_val) {
    color(color_val)
    for(i=[0:1]) mirror([i==0?1:0, 0, 0]) {
        hull() {
            translate([0, 0, 0])
                cube([thickness, base_w, base_h], center=true);
            translate([length, 0, base_h/2])
                scale([0.2, 0.5, 0.5])
                    sphere(r=base_w);
        }
    }
}

module head() {
    color([0.1, 0.5, 0.2])
    union() {
        // Main head
        translate([0, 0, 15])
            scale([1, 0.8, 1.2])
                sphere(r=10);

        // Snout
        translate([0, 0, -5])
            cylinder(h=15, r1=8, r2=5);

        // Yellow jaw markings
        color([1, 0.8, 0])
        translate([0, 5.5, 2])
            cube([12, 1, 6], center=true);

        // Eyes
        color([1, 1, 0])
        for(x=[-1, 1]) {
            translate([x*5, 7, 10])
                scale([1, 0.5, 1.5])
                    rotate([90, 0, 0])
                        cylinder(h=1, r=2);
        }

        // Red eye markings
        color([0.8, 0, 0])
        for(x=[-1, 1]) {
            translate([x*5, 7.5, 10])
                cube([4, 1, 5], center=true);
        }
    }
}

module body() {
    // Head
    translate([0, 0, 80])
        rotate([0, 20, 0])
            head();

    // Neck segments
    translate([0, 0, 70])
        body_segment(9, 8, [0.1, 0.5, 0.2]);
    translate([0, 0, 60])
        body_segment(8.5, 8, [0.1, 0.5, 0.2]);

    // Body segments
    for(i=[0:7]) {
        translate([0, 0, 50-i*6])
            body_segment(8-i*0.3, 6, [0.1, 0.5, 0.2]);
    }
}

module arms() {
    color([0.1, 0.5, 0.2])
    for(side=[-1, 1]) {
        translate([side*10, 0, 45])
            rotate([0, 0, side*30])
                cylinder(h=15, r=2.5);
    }
}

module wings() {
    color([0.1, 0.5, 0.2])
    for(side=[-1, 1]) {
        translate([side*12, 0, 55])
            rotate([0, 0, side*15])
                fin(1, 20, 25, 1, [0.1, 0.5, 0.2]);
    }
}

module tail_fin() {
    color([0.1, 0.5, 0.2])
    translate([0, 0, 5])
        rotate([0, 0, 0])
            fin(1, 15, 20, 1.5, [0.1, 0.5, 0.2]);
}

module yellow_markings() {
    color([1, 0.8, 0])
    for(i=[0:3]) {
        translate([0, 0, 30-i*10])
            cylinder(h=2, r=9-i*0.5);
    }
    translate([0, 0, 8])
        cylinder(h=2, r=6);
}

// Main assembly
scale([scale_factor, scale_factor, scale_factor])
rotate([0, 180, 0])
union() {
    body();
    arms();
    wings();
    tail_fin();
    yellow_markings();
}

