// Reshiram - Realistic style, medium complexity
// 3D printable Pokemon model with proper wall thickness

// Colors
color_white = [0.95, 0.95, 0.9];
color_blue = [0.2, 0.4, 0.8];
color_gold = [0.9, 0.7, 0.2];
color_red = [0.8, 0.1, 0.1];

// Main body module
module reshiram() {
    // Scale for 3D printing (80mm tall)
    scale(0.8) {
        // Head
        color(color_white) {
            translate([0, 0, 45]) {
                // Main head shape
                sphere(8);
                translate([0, 0, 4]) sphere(7);

                // Snout
                translate([0, 8, -2]) scale([1, 1.5, 0.8]) sphere(6);

                // Eyes
                color(color_blue) {
                    translate([4, 6, 2]) scale([1.2, 0.8, 0.5]) sphere(2);
                    translate([-4, 6, 2]) scale([1.2, 0.8, 0.5]) sphere(2);
                }

                // Eye rings
                color(color_gold) {
                    for(x = [4, -4]) {
                        translate([x, 6, 2]) rotate([0, 90, 0]) 
                            cylinder(h=0.5, r1=3, r2=2.5);
                    }
                }
            }
        }

        // Neck
        color(color_white) {
            translate([0, 0, 35]) rotate([0, 0, 0]) 
                cylinder(h=10, r1=5, r2=6);
        }

        // Torso
        color(color_white) {
            translate([0, 0, 25]) {
                sphere(8);
                translate([0, 0, -5]) cylinder(h=10, r1=8, r2=6);
            }
        }

        // Arms
        module arm(side) {
            mirror([side, 0, 0]) {
                translate([6, 4, 28]) {
                    // Upper arm
                    rotate([0, 0, -30]) {
                        cylinder(h=8, r1=2.5, r2=2);

                        // Lower arm
                        translate([0, 6, 0]) rotate([30, 0, 0]) {
                            cylinder(h=6, r1=2, r2=1.8);

                            // Hand
                            translate([0, 5, 0]) sphere(2);
                        }
                    }
                }
            }
        }

        arm(1);
        arm(-1);

        // Wings
        module wing(side) {
            mirror([side, 0, 0]) {
                translate([7, -2, 35]) {
                    rotate([0, -20, 30]) {
                        // Main wing structure
                        color(color_white) {
                            scale([1, 0.2, 2]) sphere(5);
                            translate([0, 0, 3]) scale([0.7, 0.15, 1.5]) sphere(4);
                        }

                        // Wing membrane
                        color(color_blue) {
                            translate([0, 0, 1]) scale([1.2, 0.1, 2.2]) 
                                rotate([0, 10, 0]) sphere(4);
                        }
                    }
                }
            }
        }

        wing(1);
        wing(-1);

        // Legs
        module leg(side) {
            mirror([side, 0, 0]) {
                translate([4, 0, 15]) {
                    // Upper leg
                    cylinder(h=8, r1=3, r2=2.5);

                    // Lower leg
                    translate([0, 0, -8]) rotate([15, 0, 0]) {
                        cylinder(h=7, r1=2.5, r2=2);

                        // Foot
                        translate([0, 2, -6]) rotate([90, 0, 0]) {
                            cylinder(h=4, r1=2, r2=3);
                        }
                    }
                }
            }
        }

        leg(1);
        leg(-1);

        // Tail base
        color(color_white) {
            translate([0, -8, 20]) {
                cylinder(h=5, r1=5, r2=4);
            }
        }

        // Tail segments
        color(color_white) {
            translate([0, -15, 18]) {
                rotate([90, 0, 0]) {
                    cylinder(h=15, r1=4, r2=3);

                    // Tail turbine
                    translate([0, 0, 16]) {
                        color(color_gold) {
                            cylinder(h=3, r1=3.5, r2=3.5);

                            // Turbine blades
                            for(i = [0:3]) {
                                rotate([0, 0, i * 90]) {
                                    translate([3, 0, 1.5]) 
                                        cube([4, 0.5, 3], center=true);
                                }
                            }
                        }
                    }
                }
            }
        }

        // Chest marking
        color(color_gold) {
            translate([0, 6, 28]) rotate([0, 90, 0]) 
                cylinder(h=0.5, r1=3, r2=0);
        }

        // Head crest
        color(color_white) {
            translate([0, 4, 53]) {
                for(i = [0:2]) {
                    rotate([0, 0, (i - 1) * 20]) {
                        translate([0, 2, 0]) 
                            scale([0.5, 0.5, 1]) cylinder(h=5, r1=1, r2=0.5);
                    }
                }
            }
        }
    }
}

// Generate the model
reshiram();

// Zekrom - medium complexity realistic style
// Based on Pokemon Black/White design

module zekrom_tail() {
    // Tail generator with electric turbine details
    rotate([0, -20, 0]) {
        // Main tail segments
        for(i = [0:1:4]) {
            translate([0, 0, -i*8]) {
                rotate([0, 0, i*30]) {
                    color("gray20") {
                        cylinder(h=10, r1=6-i, r2=5-i, $fn=8);
                    }
                }
            }
        }

        // Tail tip generator
        translate([0, 0, -40]) {
            // Electric turbine core
            color("royalblue") {
                sphere(8, $fn=16);
            }

            // Surrounding ring
            color("gray20") {
                rotate_extrude($fn=32) {
                    translate([9, 0, 0]) {
                        circle(r=2, $fn=16);
                    }
                }
            }

            // Spikes
            for(angle = [0:60:359]) {
                rotate([0, 0, angle]) {
                    translate([0, 10, 0]) {
                        rotate([90, 0, 0]) {
                            cylinder(h=5, r1=1.5, r2=0, $fn=6);
                        }
                    }
                }
            }
        }
    }
}

module zekrom_head() {
    // Main head structure
    translate([0, 0, 15]) {
        // Skull base
        color("gray20") {
            sphere(10, $fn=24);
        }

        // Snout extension
        translate([8, 0, 0]) {
            color("gray20") {
                rotate([0, 90, 0]) {
                    cylinder(h=15, r1=8, r2=6, $fn=16);
                }
            }
        }

        // Eyes
        for(side = [-1, 1]) {
            translate([4, side*6, 3]) {
                color("red") {
                    sphere(3, $fn=12);
                }

                // Eye glow
                translate([1, 0, 0]) {
                    color("white") {
                        sphere(1, $fn=8);
                    }
                }
            }
        }

        // Horns
        for(side = [-1, 1]) {
            translate([0, side*8, 12]) {
                rotate([0, side*20, 0]) {
                    color("gray20") {
                        cylinder(h=12, r1=3, r2=2, $fn=8);
                    }
                }
            }
        }

        // Jaw
        translate([12, 0, -5]) {
            color("gray30") {
                cube([8, 8, 4], center=true);
            }
        }
    }
}

module zekrom_wing() {
    // Dragon wing structure
    rotate([0, 0, 45]) {
        // Wing arm
        translate([0, 0, 0]) {
            color("gray20") {
                cylinder(h=20, r1=3, r2=2, $fn=8);
            }
        }

        // Wing membrane segments
        for(i = [0:1:3]) {
            translate([5+i*3, i*6, 0]) {
                rotate([0, 0, -i*20]) {
                    color("gray40") {
                        cube([8-i, 12-i*2, 0.5], center=true);
                    }
                }
            }
        }

        // Wing claws
        translate([15, 10, 0]) {
            rotate([90, 0, 0]) {
                for(j = [0:1:2]) {
                    translate([j*2-2, 0, 0]) {
                        color("gray20") {
                            cylinder(h=3, r1=1, r2=0.5, $fn=6);
                        }
                    }
                }
            }
        }
    }
}

module zekrom_body() {
    // Main torso
    color("gray20") {
        sphere(15, $fn=24);
    }

    // Chest armor detail
    translate([0, 0, 0]) {
        color("gray30") {
            cube([20, 25, 10], center=true);
        }
    }

    // Shoulder plates
    for(side = [-1, 1]) {
        translate([0, side*15, 5]) {
            color("gray30") {
                rotate([0, 30, 0]) {
                    cube([8, 12, 6], center=true);
                }
            }
        }
    }

    // Neck connector
    translate([-5, 0, 10]) {
        color("gray20") {
            cylinder(h=10, r1=8, r2=6, $fn=16);
        }
    }
}

module zekrom_arms() {
    // Arms with clawed hands
    for(side = [-1, 1]) {
        translate([5, side*15, 0]) {
            // Upper arm
            color("gray20") {
                rotate([0, 0, side*20]) {
                    cylinder(h=12, r1=3, r2=2.5, $fn=8);
                }
            }

            // Lower arm
            translate([8, side*5, -5]) {
                color("gray20") {
                    rotate([0, 20, side*30]) {
                        cylinder(h=10, r1=2.5, r2=2, $fn=8);
                    }
                }

                // Hand with claws
                translate([12, side*7, -8]) {
                    color("gray20") {
                        sphere(3, $fn=12);
                    }

                    // Claws
                    for(claw = [0:1:2]) {
                        rotate([0, 0, claw*120-120]) {
                            translate([2, 0, 0]) {
                                color("gray30") {
                                    rotate([90, 0, 0]) {
                                        cylinder(h=4, r1=1, r2=0.5, $fn=6);
                                    }
                                }
                            }
                        }
                    }
                }
            }
        }
    }
}

module zekrom_legs() {
    // Powerful dragon legs
    for(side = [-1, 1]) {
        translate([-5, side*8, -15]) {
            // Thigh
            color("gray20") {
                rotate([30, 0, side*10]) {
                    cylinder(h=15, r1=4, r2=3, $fn=8);
                }
            }

            // Lower leg
            translate([-5, side*3, -12]) {
                color("gray20") {
                    rotate([0, 0, side*15]) {
                        cylinder(h=12, r1=3, r2=2.5, $fn=8);
                    }
                }

                // Foot
                translate([-8, side*4, -18]) {
                    color("gray20") {
                        cube([10, 6, 4], center=true);
                    }

                    // Toes
                    for(toe = [0:1:2]) {
                        translate([-12+toe*3, side*2, -20]) {
                            color("gray30") {
                                cube([2, 2, 3], center=true);
                            }
                        }
                    }
                }
            }
        }
    }
}

