// Colors
color_body = [0.85,0.9,0.95];
color_ice = [0.7,0.9,1];
color_gray = [0.4,0.4,0.45];
color_yellow = [1,0.8,0];
color_red = [0.8,0.1,0.1];

// Main body
module kyurem() {
    union() {
        // Head
        translate([0,0,60]) head();

        // Neck/upper body
        translate([0,0,45]) neck();

        // Torso
        translate([0,0,25]) torso();

        // Wings
        translate([-15,0,35]) wing(true);
        translate([15,0,35]) wing(false);

        // Arms
        translate([-12,0,35]) arm(true);
        translate([12,0,35]) arm(false);

        // Legs
        translate([-8,-5,10]) leg(true);
        translate([8,-5,10]) leg(false);

        // Tail
        translate([0,-8,20]) tail();

        // Ice spikes
        ice_spikes();
    }
}

module head() {
    color(color_body) {
        // Main head shape
        difference() {
            sphere(12);
            translate([0,-12,0]) cube([24,24,24], center=true);
        }

        // Snout
        translate([0,-10,0]) scale([0.8,1.2,0.8]) sphere(8);

        // Eyes
        color(color_yellow) {
            translate([-5,5,2]) sphere(2);
            translate([5,5,2]) sphere(2);
        }

        // Eye pupils
        color(color_red) {
            translate([-5,6,2.5]) rotate([0,90,0]) cylinder(h=1, r=0.5);
            translate([5,6,2.5]) rotate([0,90,0]) cylinder(h=1, r=0.5);
        }
    }
}

module neck() {
    color(color_body) {
        cylinder(h=15, r1=8, r2=10, center=true);
    }
}

module torso() {
    color(color_body) {
        // Main body
        difference() {
            scale([1.2,0.8,1.5]) sphere(15);
            // Chest cavity
            translate([0,12,0]) cube([20,20,25], center=true);
        }

        // Ice patterns on chest
        color(color_ice) {
            for(i=[-15:5:15]) {
                translate([i,0,20]) rotate([0,90,0]) cylinder(h=2, r=1.5);
            }
        }
    }
}

module wing(left=true) {
    color(color_body) {
        mirror([left?-1:1,0,0]) {
            // Wing base
            translate([0,0,0]) rotate([0,20,30]) scale([0.5,1,0.1]) sphere(20);

            // Wing membranes
            color(color_ice) {
                for(i=[0:30:120]) {
                    rotate([0,i,0]) translate([0,0,0]) 
                    rotate([0,20,0]) scale([0.3,0.8,0.05]) sphere(15);
                }
            }
        }
    }
}

module arm(left=true) {
    color(color_body) {
        mirror([left?-1:1,0,0]) {
            // Upper arm
            rotate([0,0,left?20:-20]) translate([0,0,-5]) 
            cylinder(h=12, r=3, center=true);

            // Lower arm
            translate([0,-8,-10]) rotate([0,0,left?40:-40])
            cylinder(h=10, r=2.5, center=true);

            // Claws
            color(color_gray) {
                for(i=[-2:2:2]) {
                    translate([i,-13,-14]) rotate([0,0,left?-20:20])
                    cylinder(h=3, r1=1, r2=0.5);
                }
            }
        }
    }
}

module leg(left=true) {
    color(color_body) {
        mirror([left?-1:1,0,0]) {
            // Upper leg
            cylinder(h=15, r1=4, r2=3.5, center=true);

            // Lower leg
            translate([0,0,-15]) cylinder(h=12, r1=3.5, r2=3);

            // Foot
            translate([0,3,-24]) scale([1.5,2,0.8]) sphere(3);

            // Toes
            color(color_gray) {
                for(i=[-2:2:2]) {
                    translate([i,5,-26]) sphere(1);
                }
            }
        }
    }
}

module tail() {
    color(color_body) {
        // Tail base
        cylinder(h=20, r1=5, r2=3);

        // Tail tip
        translate([0,-15,0]) color(color_ice) sphere(4);

        // Tail segments
        for(i=[0:5:15]) {
            translate([0,-i-5,i/3]) rotate([0,15,0])
            scale([0.8,1,0.8]) sphere(3);
        }
    }
}

module ice_spikes() {
    color(color_ice) {
        // Back spikes
        for(i=[0:10:40]) {
            translate([0,0,i+20]) rotate([0,0,sin(i*5)*10])
            cylinder(h=3+sin(i*3)*2, r1=2, r2=0.5);
        }

        // Shoulder spikes
        translate([-15,0,35]) rotate([0,0,-30]) cylinder(h=5, r1=1.5, r2=0.5);
        translate([15,0,35]) rotate([0,0,30]) cylinder(h=5, r1=1.5, r2=0.5);

        // Head spikes
        translate([0,0,70]) rotate([0,0,0]) cylinder(h=6, r1=2, r2=0.5);
        translate([-5,0,68]) rotate([0,0,-20]) cylinder(h=4, r1=1.5, r2=0.5);
        translate([5,0,68]) rotate([0,0,20]) cylinder(h=4, r1=1.5, r2=0.5);
    }
}

