// Realistic medium-complexity Squirtle for 3D printing

// Colors
shell_color = [0.2, 0.6, 0.8];
belly_color = [0.9, 0.9, 0.8];
eye_color = [0.9, 0.9, 0.9];
pupil_color = [0.1, 0.1, 0.1];
tail_color = [0.6, 0.8, 0.9];

// Overall scale
module squirtle() {
    scale([1, 1, 1.2]) {
        // Main body shell
        color(shell_color) difference() {
            sphere(d=45, $fn=32);
            translate([0, 0, -25]) cube([50, 50, 50], center=true);
            translate([0, 0, 20]) cube([40, 40, 20], center=true);
        }

        // Belly
        color(belly_color) translate([0, -22.5, 5]) {
            scale([1, 0.7, 0.9]) sphere(d=43, $fn=32);
        }

        // Head
        color(shell_color) translate([0, 0, 35]) {
            sphere(d=35, $fn=32);
        }

        // Snout
        color(shell_color) translate([0, 15, 30]) {
            rotate([30, 0, 0]) scale([1, 1.2, 0.8]) sphere(d=20, $fn=32);
        }

        // Eyes
        for (x = [-8, 8]) {
            color(eye_color) translate([x, 18, 40]) {
                sphere(d=8, $fn=16);
            }
            color(pupil_color) translate([x + 2, 18, 43]) {
                sphere(d=4, $fn=16);
            }
        }

        // Mouth
        color([0.2, 0.1, 0.1]) translate([0, 25, 32]) {
            rotate([90, 0, 0]) cylinder(h=3, r1=5, r2=0, $fn=16);
        }

        // Arms
        module arm(side) {
            translate([side * 20, 0, 15]) {
                rotate([0, 0, side * 15]) {
                    color(shell_color) {
                        sphere(d=10, $fn=16);
                        translate([0, 0, -7]) cylinder(h=15, r=4, $fn=16);
                    }
                }
            }
        }
        arm(1);
        arm(-1);

        // Legs
        module leg(side) {
            translate([side * 15, 0, -15]) {
                color(shell_color) {
                    sphere(d=12, $fn=16);
                    translate([0, 0, -8]) cylinder(h=12, r=5, $fn=16);
                }
            }
        }
        leg(1);
        leg(-1);

        // Tail
        color(tail_color) translate([0, -20, -10]) {
            rotate([0, 0, -20]) {
                union() {
                    cylinder(h=25, r1=8, r2=6, $fn=16);
                    translate([0, 0, 25]) {
                        rotate([0, 0, 30]) {
                            scale([1.5, 1, 0.3]) sphere(d=20, $fn=16);
                        }
                    }
                }
            }
        }

        // Shell pattern lines
        module shell_line(angle) {
            color([0.1, 0.3, 0.4]) rotate([0, 0, angle]) {
                translate([0, 0, 22]) {
                    for (r = [15:4:35]) {
                        rotate_extrude(angle=30, $fn=32) {
                            translate([r, 0, 0]) square([0.5, 2]);
                        }
                    }
                }
            }
        }

        shell_line(0);
        shell_line(120);
        shell_line(240);

        // Tail curl pattern
        color([0.4, 0.5, 0.7]) translate([0, -30, 5]) {
            rotate([90, 0, 0]) {
                for (a = [0:45:315]) {
                    rotate([0, 0, a]) {
                        translate([12, 0, 0]) sphere(d=2, $fn=8);
                    }
                }
            }
        }
    }
}

