// Main shell
module metapod_shell() {
    color([0.6, 0.75, 0.5]) {
        // Central pod body
        difference() {
            // Outer shell
            translate([0,0,overall_height/2])
                scale([1, 0.7, 0.5])
                    sphere(d=overall_height*1.8, $fn=64);

            // Hollow interior
            translate([0,0,overall_height/2])
                scale([1, 0.7, 0.5])
                    sphere(d=overall_height*1.8 - shell_thickness*2, $fn=64);
        }

        // Front taper
        translate([overall_length/2 - 5, 0, overall_height/2])
            scale([1.2, 0.8, 0.4])
                sphere(d=overall_height*1.5, $fn=48);

        // Back taper
        translate([-overall_length/2 + 5, 0, overall_height/2])
            scale([1.2, 0.8, 0.4])
                sphere(d=overall_height*1.5, $fn=48);
    }
}

// Segmented shell pattern
module shell_segments() {
    color([0.5, 0.65, 0.4]) {
        for(i = [-3:3]) {
            // Vertical segments
            rotate([0, 0, i*20])
                translate([0, 0, overall_height/2])
                    scale([1, 0.7, 0.5])
                        difference() {
                            cylinder(h=overall_height+10, r=overall_height*0.9, $fn=3);
                            translate([0,0,-1])
                                cylinder(h=overall_height+12, r=overall_height*0.9-1, $fn=3);
