    color([0.1, 0.1, 0.1]) {
        // Left eye slit
        translate([overall_length/3, -overall_height/3, overall_height/2 + 2])
            rotate([0, 25, 0])
                scale([1, 0.3, 0.1])
                    cube([8, 3, 2], center=true);

        // Right eye slit
        translate([overall_length/3, overall_height/3, overall_height/2 + 2])
            rotate([0, 25, 0])
                scale([1, 0.3, 0.1])
                    cube([8, 3, 2], center=true);
